import React, { useState } from "react";
import axios from "axios";

const CreateAssessment = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [questions, setQuestions] = useState([
    { questionText: "", options: ["", "", "", ""], correctAnswer: "" },
  ]);

  const handleAddQuestion = () => {
    setQuestions([...questions, { questionText: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleChangeQuestion = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].questionText = value;
    setQuestions(newQuestions);
  };

  const handleChangeOption = (qIndex, optIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[optIndex] = value;
    setQuestions(newQuestions);
  };

  const handleSelectCorrectAnswer = (qIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].correctAnswer = value;
    setQuestions(newQuestions);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:5000/api/assessments/create",
        { title, description, questions },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("‚úÖ Assessment Created Successfully!");
    } catch (error) {
      console.error("Error creating assessment", error);
      alert("‚ùå Failed to create assessment.");
    }
  };

  return (
    <section
      style={{
        marginLeft: "250px", // Sidebar width
        width: "calc(100% - 250px)",
        padding: "30px",
        minHeight: "100vh",
        backgroundColor: "#f8f9fa",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          background: "#ffffff",
          padding: "30px",
          borderRadius: "12px",
          width: "500px",
          boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
        }}
      >
        <h2 style={{ fontSize: "24px", fontWeight: "700", color: "#003c8f", textAlign: "center" }}>
          üìù Create Assessment
        </h2>
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
          <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Title:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              fontSize: "16px",
            }}
          />

          <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Description:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            rows="3"
            style={{
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              fontSize: "16px",
              resize: "none",
            }}
          />

          {questions.map((q, qIndex) => (
            <div key={qIndex} style={{ background: "#f8f9fa", padding: "15px", borderRadius: "10px" }}>
              <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>
                Question {qIndex + 1}:
              </label>
              <input
                type="text"
                value={q.questionText}
                onChange={(e) => handleChangeQuestion(qIndex, e.target.value)}
                required
                style={{
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  fontSize: "16px",
                }}
              />

              {q.options.map((opt, optIndex) => (
                <input
                  key={optIndex}
                  type="text"
                  placeholder={`Option ${optIndex + 1}`}
                  value={opt}
                  onChange={(e) => handleChangeOption(qIndex, optIndex, e.target.value)}
                  required
                  style={{
                    padding: "10px",
                    border: "1px solid #ccc",
                    borderRadius: "8px",
                    fontSize: "16px",
                    marginTop: "5px",
                  }}
                />
              ))}

              <select
                value={q.correctAnswer}
                onChange={(e) => handleSelectCorrectAnswer(qIndex, e.target.value)}
                required
                style={{
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                  fontSize: "16px",
                  marginTop: "10px",
                  background: "white",
                }}
              >
                <option value="">Select Correct Answer</option>
                {q.options.map((opt, optIndex) => (
                  <option key={optIndex} value={opt}>
                    {opt}
                  </option>
                ))}
              </select>
            </div>
          ))}

          <button
            type="button"
            onClick={handleAddQuestion}
            style={{
              backgroundColor: "#6c757d",
              color: "white",
              padding: "12px",
              fontSize: "16px",
              fontWeight: "bold",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "all 0.3s ease",
            }}
          >
            ‚ûï Add Question
          </button>

          <button
            type="submit"
            style={{
              backgroundColor: "#007bff",
              color: "white",
              padding: "12px",
              fontSize: "16px",
              fontWeight: "bold",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "all 0.3s ease",
              marginTop: "10px",
            }}
          >
            ‚úÖ Create Assessment
          </button>
        </form>
      </div>
    </section>
  );
};

export default CreateAssessment;
