import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CreateCourse = ({ role }) => {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [course, setCourse] = useState({
        title: "",
        desc: "",
        price: "",
        img: "",
    });

    // Redirect non-admin users
    useEffect(() => {
        if (role !== "admin") {
            navigate("/");
        }
    }, [role, navigate]);

    const handleChange = (e) => {
        setCourse({ ...course, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setLoading(true);

        if (!course.img.startsWith("http")) {
            setError("Please enter a valid image URL.");
            setLoading(false);
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/courses", course);
            alert("🎉 Course added successfully!");
            navigate("/courses");
        } catch (error) {
            setError(error.response?.data?.message || "Something went wrong.");
            console.error("Error adding course:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">➕ Add New Course</h2>
            {error && <p className="alert alert-danger">{error}</p>}

            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">📖 Title</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="title" 
                        value={course.title} 
                        onChange={handleChange} 
                        required 
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label">📝 Description</label>
                    <textarea 
                        className="form-control" 
                        name="desc" 
                        value={course.desc} 
                        onChange={handleChange} 
                        required 
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label">💰 Price</label>
                    <input 
                        type="number" 
                        className="form-control" 
                        name="price" 
                        value={course.price} 
                        onChange={handleChange} 
                        required 
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label">🖼️ Image URL</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="img" 
                        value={course.img} 
                        onChange={handleChange} 
                        required 
                    />
                    {course.img && course.img.startsWith("http") && (
                        <div className="mt-3">
                            <img src={course.img} alt="Preview" style={{ maxWidth: "100%", height: "200px", borderRadius: "8px" }} />
                        </div>
                    )}
                    <small className="text-muted">Provide a valid image URL (e.g., https://example.com/image.jpg)</small>
                </div>

                <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? "Adding..." : "Add Course"}
                </button>
            </form>
        </div>
    );
};

export default CreateCourse;
