import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const EditCourse = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState({ title: "", desc: "", price: "", img: "", video: "" });

    useEffect(() => {
        fetch(`http://localhost:5000/api/courses/${id}`)
            .then((res) => res.json())
            .then((data) => setCourse(data))
            .catch((err) => console.error("Error fetching course:", err));
    }, [id]);

    const handleChange = (e) => {
        setCourse({ ...course, [e.target.name]: e.target.value });
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:5000/api/courses/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(course),
            });

            if (response.ok) {
                alert("Course updated successfully!");
                navigate("/courses");
            } else {
                alert("Failed to update course");
            }
        } catch (error) {
            console.error("Error updating course:", error);
        }
    };

    return (
        <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
            <h2>Edit Course</h2>
            <form onSubmit={handleUpdate}>
                <label>Title:</label>
                <input type="text" name="title" value={course.title} onChange={handleChange} required />

                <label>Description:</label>
                <textarea name="desc" value={course.desc} onChange={handleChange} required />

                <label>Price:</label>
                <input type="text" name="price" value={course.price} onChange={handleChange} required />

                <label>Image URL:</label>
                <input type="text" name="img" value={course.img} onChange={handleChange} required />

                <label>Video URL:</label>
                <input type="text" name="video" value={course.video} onChange={handleChange} />

                <button type="submit">Update Course</button>
            </form>
        </div>
    );
};

export default EditCourse;
