import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [summary, setSummary] = useState({});
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [assessments, setAssessments] = useState([]);
  const [submissions, setSubmissions] = useState([]);

  const userId = localStorage.getItem("userId"); // Admin user ID

  useEffect(() => {
    fetchSummary();
    fetchUsers();
    fetchCourses();
    fetchAssessments();
    fetchSubmissions();
  }, []);

  const fetchSummary = async () => {
    const res = await axios.get(`http://localhost:5000/api/admin/summary?userId=${userId}`);
    setSummary(res.data);
  };

  const fetchUsers = async () => {
    const res = await axios.get(`http://localhost:5000/api/admin/users?userId=${userId}`);
    setUsers(res.data);
  };

  const fetchCourses = async () => {
    const res = await axios.get(`http://localhost:5000/api/admin/courses?userId=${userId}`);
    setCourses(res.data);
  };

  const fetchAssessments = async () => {
    const res = await axios.get(`http://localhost:5000/api/admin/assessments?userId=${userId}`);
    setAssessments(res.data);
  };

  const fetchSubmissions = async () => {
    const res = await axios.get(`http://localhost:5000/api/admin/submissions?userId=${userId}`);
    setSubmissions(res.data);
  };

  return (
    <div className="admin-dashboard">
      <style>
        {`
          /* Sidebar */
          .sidebar {
            position: fixed;
            top: 0;
            left: 0;
            width: 250px;
            height: 100vh;
            background-color: #071d5a;
            color: white;
            padding-top: 20px;
            transition: all 0.3s ease-in-out;
            overflow-y: auto;
            z-index: 1000;
            box-shadow: 2px 0 10px rgba(0, 0, 0, 0.2);
            display: flex;
            flex-direction: column;
            align-items: center;
          }

          .sidebar a {
            color: #fff;
            text-decoration: none;
            font-size: 18px;
            display: block;
            padding: 10px;
            margin-bottom: 10px;
          }

          .sidebar a:hover {
            background-color: #575757;
          }

          /* Layout container */
          .admin-dashboard {
            display: flex;
          }

          .dashboard-container {
            padding: 20px;
            margin-left: 250px;
            flex: 1;
            max-width: 1200px;
            margin: auto;
          }

          .card-container {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(240px, 1fr));
            gap: 20px;
            margin-bottom: 40px;
          }

          .card {
            background-color: #ffffff;
            padding: 20px;
            border-radius: 12px;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
            text-align: center;
          }

          .card h3 {
            font-size: 1.5rem;
            font-weight: bold;
            color: #007bff;
          }

          .card p {
            font-size: 1.2rem;
            margin-top: 10px;
          }

          .table-container {
            margin-top: 30px;
            margin-bottom: 30px;
          }

          table {
            width: 100%;
            border-collapse: collapse;
            border-radius: 10px;
            overflow: hidden;
            background-color: #fff;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
          }

          th,
          td {
            padding: 15px;
            text-align: left;
            border-top: 1px solid #ddd;
          }

          th {
            background-color: #f7f7f7;
            font-weight: bold;
          }

          tr:hover {
            background-color: #f1f1f1;
          }

          .table-container h2 {
            font-size: 1.75rem;
            font-weight: bold;
            color: #333;
            margin-bottom: 20px;
          }

          .table-container td,
          .table-container th {
            font-size: 1rem;
            color: #333;
          }
        `}
      </style>

      {/* Sidebar */}
      <div className="sidebar">
        <h2>Admin Menu</h2>
        <a href="#">Dashboard</a>
        <a href="#">Users</a>
        <a href="#">Courses</a>
        <a href="#">Assessments</a>
        <a href="#">Submissions</a>
      </div>

      {/* Main Dashboard */}
      <div className="dashboard-container">
        <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

        {/* Summary Cards */}
        <div className="card-container">
          <div className="card">
            <h3>Users</h3>
            <p>{summary.totalUsers ?? 0}</p>
          </div>
          <div className="card">
            <h3>Courses</h3>
            <p>{summary.totalCourses ?? 0}</p>
          </div>
          <div className="card">
            <h3>Assessments</h3>
            <p>{summary.totalAssessments ?? 0}</p>
          </div>
          <div className="card">
            <h3>Submissions</h3>
            <p>{summary.totalSubmissions ?? 0}</p>
          </div>
        </div>

        {/* Tables */}
        <div className="table-container">
          <Section title="Users" data={users} columns={["fullName", "email", "role"]} />
          <Section title="Courses" data={courses} columns={["title", "description"]} />
          <Section title="Assessments" data={assessments} columns={["title", "courseId"]} />
          <Section title="Submissions" data={submissions} columns={["userId", "assessmentId", "score"]} />
        </div>
      </div>
    </div>
  );
};

const Section = ({ title, data, columns }) => (
  <div className="table-container">
    <h2>{title}</h2>
    <div className="overflow-x-auto">
      <table>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col} className="px-4 py-2 text-left capitalize">
                {col}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data?.length > 0 ? (
            data.map((item, idx) => (
              <tr key={idx}>
                {columns.map((col) => (
                  <td key={col} className="px-4 py-2">
                    {item[col]?.title || item[col]?.fullName || item[col] || "-"}
                  </td>
                ))}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={columns.length} className="px-4 py-2 text-center">
                No data found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  </div>
);

export default AdminDashboard;
