import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CreateCourse = ({ role }) => {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [course, setCourse] = useState({
        title: "",
        desc: "",
        price: "",
        img: "",
        videoUrl: "" // Added video URL field
    });

    // Prevent access if role is undefined/null
    const [checkingRole, setCheckingRole] = useState(true);

    useEffect(() => {
        console.log("Role in CreateCourse:", role);
        if (role === undefined || role === null) {
            return;
        }
        if (role !== "admin") {
            navigate("/");
        }
        setCheckingRole(false);
    }, [role, navigate]);

    const handleChange = (e) => {
        setCourse({ ...course, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setLoading(true);

        if (!course.img.startsWith("http")) {
            setError("Please enter a valid image URL.");
            setLoading(false);
            return;
        }

        if (course.videoUrl && !course.videoUrl.startsWith("http")) {
            setError("Please enter a valid video URL.");
            setLoading(false);
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/courses", course);
            alert("üéâ Course added successfully!");
            navigate("/courses");
        } catch (error) {
            setError(error.response?.data?.message || "Something went wrong.");
            console.error("Error adding course:", error);
        } finally {
            setLoading(false);
        }
    };

    if (checkingRole) {
        return <h3 style={{ textAlign: "center", marginTop: "50px" }}>üîÑ Checking access...</h3>;
    }

    return (
        <section 
            style={{
                marginLeft: "250px",
                width: "calc(100% - 250px)",
                padding: "30px",
                minHeight: "100vh",
                backgroundColor: "#f8f9fa",
            }}
        >
            <div 
                style={{
                    backgroundColor: "#ffffff",
                    padding: "25px",
                    borderRadius: "10px",
                    boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
                    maxWidth: "600px",
                    margin: "auto",
                    textAlign: "center"
                }}
            >
                <h2 style={{ fontSize: "26px", fontWeight: "800", color: "#003c8f", marginBottom: "20px" }}>
                    ‚ûï Add New Course
                </h2>

                {error && (
                    <p style={{ color: "red", fontWeight: "bold", marginBottom: "15px" }}>
                        ‚ùå {error}
                    </p>
                )}

                <form onSubmit={handleSubmit} style={{ textAlign: "left" }}>
                    <label style={{ fontSize: "16px", fontWeight: "bold", color: "#333" }}>üìñ Course Title</label>
                    <input 
                        type="text" 
                        name="title" 
                        value={course.title} 
                        onChange={handleChange} 
                        required 
                        style={{
                            width: "100%",
                            padding: "10px",
                            marginBottom: "15px",
                            border: "1px solid #ccc",
                            borderRadius: "6px",
                            fontSize: "16px"
                        }}
                    />

                    <label style={{ fontSize: "16px", fontWeight: "bold", color: "#333" }}>üìù Description</label>
                    <textarea 
                        name="desc" 
                        value={course.desc} 
                        onChange={handleChange} 
                        required 
                        style={{
                            width: "100%",
                            padding: "10px",
                            marginBottom: "15px",
                            border: "1px solid #ccc",
                            borderRadius: "6px",
                            fontSize: "16px",
                            resize: "none",
                            height: "100px"
                        }}
                    />

                    <label style={{ fontSize: "16px", fontWeight: "bold", color: "#333" }}>üí∞ Price</label>
                    <input 
                        type="number" 
                        name="price" 
                        value={course.price} 
                        onChange={handleChange} 
                        required 
                        style={{
                            width: "100%",
                            padding: "10px",
                            marginBottom: "15px",
                            border: "1px solid #ccc",
                            borderRadius: "6px",
                            fontSize: "16px"
                        }}
                    />

                    <label style={{ fontSize: "16px", fontWeight: "bold", color: "#333" }}>üñºÔ∏è Image URL</label>
                    <input 
                        type="text" 
                        name="img" 
                        value={course.img} 
                        onChange={handleChange} 
                        required 
                        style={{
                            width: "100%",
                            padding: "10px",
                            marginBottom: "15px",
                            border: "1px solid #ccc",
                            borderRadius: "6px",
                            fontSize: "16px"
                        }}
                    />

                    {course.img && course.img.startsWith("http") && (
                        <div style={{ marginBottom: "15px", textAlign: "center" }}>
                            <img 
                                src={course.img} 
                                alt="Preview" 
                                style={{ 
                                    width: "100%", 
                                    height: "200px", 
                                    borderRadius: "8px", 
                                    objectFit: "cover", 
                                    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)" 
                                }} 
                            />
                        </div>
                    )}

                    <label style={{ fontSize: "16px", fontWeight: "bold", color: "#333" }}>üé• Video URL</label>
                    <input 
                        type="text" 
                        name="videoUrl" 
                        value={course.videoUrl} 
                        onChange={handleChange} 
                        placeholder="Enter video URL" 
                        style={{
                            width: "100%",
                            padding: "10px",
                            marginBottom: "15px",
                            border: "1px solid #ccc",
                            borderRadius: "6px",
                            fontSize: "16px"
                        }}
                    />

                   {/* Video Preview */}
{course.videoUrl && course.videoUrl.startsWith("http") && (
    <div style={{ marginBottom: "15px", textAlign: "center" }}>
        {course.videoUrl.includes("youtube.com") || course.videoUrl.includes("youtu.be") ? (
            // Embed YouTube video using iframe
            <iframe
                width="100%"
                height="315"
                src={`https://www.youtube.com/embed/${new URL(course.videoUrl).searchParams.get("v") || course.videoUrl.split("/").pop()}`}
                title="YouTube video player"
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{ borderRadius: "8px", boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)" }}
            ></iframe>
        ) : (
            // Handle other direct video URLs
            <video
                controls
                width="100%"
                style={{ borderRadius: "8px", boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)" }}
            >
                <source src={course.videoUrl} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
        )}
    </div>
)}


                    <button 
                        type="submit" 
                        disabled={loading}
                        style={{
                            backgroundColor: "#007bff",
                            color: "white",
                            fontSize: "16px",
                            fontWeight: "bold",
                            padding: "12px 18px",
                            border: "none",
                            borderRadius: "8px",
                            cursor: "pointer",
                            width: "100%",
                            transition: "all 0.3s ease",
                        }}
                    >
                        {loading ? "Adding..." : "Add Course"}
                    </button>
                </form>
            </div>
        </section>
    );
};

export default CreateCourse;
