import React, { useState, useEffect } from "react";
import axios from "axios";

const GeneralDiscussions = () => {
    const [discussions, setDiscussions] = useState([]);
    const [newDiscussion, setNewDiscussion] = useState({ title: "", content: "" });
    const [selectedDiscussion, setSelectedDiscussion] = useState(null);
    const [newReply, setNewReply] = useState("");

    useEffect(() => {
        fetchDiscussions();
    }, []);

    const fetchDiscussions = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/discussions");
            setDiscussions(res.data);
        } catch (error) {
            console.error("Error fetching discussions:", error);
        }
    };

    const handleCreateDiscussion = async () => {
        if (!newDiscussion.title || !newDiscussion.content) {
            return alert("Please enter all fields");
        }

        try {
            const userId = localStorage.getItem("userId");

            await axios.post("http://localhost:5000/api/discussions", {
                title: newDiscussion.title,
                content: newDiscussion.content,
                author: userId,
                category: "General Discussions",  // Explicitly setting category
            });

            setNewDiscussion({ title: "", content: "", category: "General Discussions" });  // Reset state properly
            fetchDiscussions();
        } catch (error) {
            console.error("Error creating discussion:", error.response?.data || error);
        }
    };

    const handleDeleteDiscussion = async (discussionId) => {
        const confirmDelete = window.confirm("Are you sure you want to delete this discussion?");
        if (!confirmDelete) return;

        try {
            await axios.delete(`http://localhost:5000/api/discussions/${discussionId}`);
            setDiscussions(discussions.filter(d => d._id !== discussionId));
        } catch (error) {
            console.error("Error deleting discussion:", error);
        }
    };



    const handleReply = async (discussionId) => {
        if (!newReply) return alert("Enter a reply");
        try {
            const userId = localStorage.getItem("userId");

            await axios.post("http://localhost:5000/api/replies", {
                content: newReply,
                author: userId,
                discussion: discussionId
            });

            setNewReply("");
            fetchDiscussions();
        } catch (error) {
            console.error("Error adding reply:", error);
        }
    };

    const handleReplyClick = async (discussionId) => {
        setSelectedDiscussion(discussionId);

        try {
            const res = await axios.get(`http://localhost:5000/api/discussions/${discussionId}`);
            const updatedDiscussion = res.data;

            setDiscussions((prev) => 
                prev.map((d) => d._id === discussionId ? updatedDiscussion : d)
            );
        } catch (error) {
            console.error("Error fetching discussion with replies:", error);
        }
    };


    const handleLike = async (discussionId) => {
        try {
            const userId = localStorage.getItem("userId");

            const res = await axios.post("http://localhost:5000/api/likes", { 
                user: userId, 
                targetId: discussionId, 
                targetType: "Discussion" 
            });

            setDiscussions((prevDiscussions) =>
                prevDiscussions.map((discussion) =>
                    discussion._id === discussionId 
                        ? { ...discussion, likes: [...discussion.likes, userId] } // Add the user to the likes array
                        : discussion
                )
            );

        } catch (error) {
            console.error("Error liking discussion:", error.response?.data || error);

            // Handle "Already liked" case without modifying the state
            if (error.response?.data?.message === "Already liked") {
                alert("User already liked this discussion.");
            }
        }
    };






    return (
        <div style={styles.container}>
            <h1 style={styles.title}>üí¨ General Discussions</h1>

            <div style={styles.form}>
                <input
                    type="text"
                    placeholder="Discussion Title"
                    value={newDiscussion.title}
                    onChange={(e) => setNewDiscussion({ ...newDiscussion, title: e.target.value })}
                    style={styles.input}
                />
                <textarea
                    placeholder="Discussion Content"
                    value={newDiscussion.content}
                    onChange={(e) => setNewDiscussion({ ...newDiscussion, content: e.target.value })}
                    style={styles.textarea}
                />
                <button onClick={handleCreateDiscussion} style={styles.button}>Start Discussion</button>
            </div>

            <div style={styles.discussionsList}>
                {discussions.map((discussion) => (
                    <div key={discussion._id} style={styles.discussionCard}>
                        <div style={styles.header}>
                            <h3>{discussion.title}</h3>
                            <span style={styles.deleteIcon} onClick={() => handleDeleteDiscussion(discussion._id)}>üóëÔ∏è</span>
                        </div>
                        <p>{discussion.content}</p>
                        <p><strong>Author:</strong> {discussion.author?.fullName || "Unknown"}</p>
                        <div style={styles.actions}>
                            <button onClick={() => handleLike(discussion._id)} style={styles.likeButton}>üëç {discussion.likes?.length || 0}</button>
                            <button onClick={() => handleReplyClick(discussion._id)} style={styles.replyButton}>
    üí¨ Reply
</button>

                        </div>

                        {/* Show all replies under each discussion */}
                        <div style={styles.replySection}>
                            {discussion.replies?.map((reply) => (
                                <p key={reply._id} style={styles.reply}>
                                    <strong>{reply.author?.fullName || "Anonymous"}:</strong> {reply.content}
                                </p>
                            ))}
                        </div>

                        {selectedDiscussion === discussion._id && (
                            <div style={styles.replyInputSection}>
                                <textarea
                                    placeholder="Write a reply..."
                                    value={newReply}
                                    onChange={(e) => setNewReply(e.target.value)}
                                    style={styles.textarea}
                                />
                                <button onClick={() => handleReply(discussion._id)} style={styles.replyButton}>
                                    ‚û§ Submit Reply
                                </button>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

const styles = {
    container: {
        marginLeft:"250px",
        padding: "30px",
        minHeight: "100vh",
        backgroundColor: "#f8f9fa",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
    },
    title: {
        fontSize: "24px",
        fontWeight: "bold",
        color: "#003c8f",
    },
    form: {
        background: "#ffffff",
        padding: "20px",
        borderRadius: "12px",
        boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
        width: "90%",
        maxWidth: "600px",
        display: "flex",
        flexDirection: "column",
        gap: "10px",
    },
    input: {
        width: "100%",
        padding: "10px",
        border: "1px solid #ccc",
        borderRadius: "8px",
        fontSize: "16px",
    },
    textarea: {
        width: "100%",
        padding: "10px",
        border: "1px solid #ccc",
        borderRadius: "8px",
        fontSize: "16px",
    },
    button: {
        padding: "10px",
        border: "none",
        borderRadius: "8px",
        background: "#003c8f",
        color: "white",
        cursor: "pointer",
        fontSize: "16px",
    },
    discussionsList: {
        width: "90%",
        maxWidth: "600px",
        marginTop: "20px",
    },
    discussionCard: {
        background: "#ffffff",
        padding: "20px",
        borderRadius: "12px",
        boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
        marginBottom: "15px",
    },
    actions: {
        display: "flex",
        gap: "10px",
        marginTop: "10px",
    },
    likeButton: {
        padding: "6px 10px",
        borderRadius: "8px",
        background: "#28a745",
        color: "white",
    },
    replyButton: {
        padding: "6px 10px",
        borderRadius: "8px",
        background: "#003c8f",
        color: "white",
    },
    header: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center"
    },
    deleteIcon: {
        cursor: "pointer",
        fontSize: "18px",
        color: "red"
    }
};

export default GeneralDiscussions;
