import React, { useEffect, useState } from "react";
import axios from "axios";

const PersonalInfo = () => {
  const [personalInfo, setPersonalInfo] = useState(null);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    dob: "",
    gender: "",
    role: "",
  });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      setError("User ID not found. Please log in again.");
      return;
    }

    axios
      .get(`http://localhost:5000/api/profile/personal-info/${userId}`)
      .then((response) => {
        const { fullName, email, phone, dob, gender, role } = response.data;
        setPersonalInfo(response.data);
        setFormData({
          fullName,
          email,
          phone,
          dob: dob || "",
          gender,
          role,
        });
      })
      .catch((error) => {
        console.error("Error fetching personal info:", error);
        setError("Failed to load personal info.");
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = () => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      alert("User ID is missing.");
      return;
    }

    axios
      .put(`http://localhost:5000/api/profile/update-personal-info/${userId}`, formData)
      .then((response) => {
        setPersonalInfo(response.data);
        setIsEditing(false);
      })
      .catch((error) => {
        console.error("Error updating personal info:", error);
        setError("Failed to update personal info.");
      });
  };

  // CSS-in-JS Styles
  const styles = {
    wrapper: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
      backgroundColor: "#f4f4f4",
    },
    container: {
      width: "400px",
      padding: "20px",
      background: "white",
      boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
      borderRadius: "8px",
      textAlign: "center",
    },
    errorText: {
      color: "red",
      fontSize: "14px",
      marginBottom: "10px",
    },
    input: {
      width: "100%",
      padding: "8px",
      marginTop: "5px",
      border: "1px solid #ccc",
      borderRadius: "4px",
      fontSize: "14px",
    },
    button: {
      margin: "10px 5px",
      padding: "10px 15px",
      fontSize: "14px",
      fontWeight: "bold",
      border: "none",
      borderRadius: "4px",
      cursor: "pointer",
      transition: "0.3s",
    },
    saveButton: {
      backgroundColor: "#007bff",
      color: "white",
    },
    saveButtonHover: {
      backgroundColor: "#0056b3",
    },
    cancelButton: {
      backgroundColor: "#dc3545",
      color: "white",
    },
    cancelButtonHover: {
      backgroundColor: "#a71d2a",
    },
    label: {
      display: "block",
      margin: "10px 0",
      textAlign: "left",
      fontWeight: "bold",
      color: "#444",
    },
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.container}>
        <h2>Personal Info</h2>
        {error && <p style={styles.errorText}>{error}</p>}
        {personalInfo ? (
          <div>
            {isEditing ? (
              <div>
                <label style={styles.label}>
                  <strong>Full Name:</strong>
                  <input
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                    style={styles.input}
                  />
                </label>
                <label style={styles.label}>
                  <strong>Email:</strong>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    style={styles.input}
                  />
                </label>
                <label style={styles.label}>
                  <strong>Phone:</strong>
                  <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    style={styles.input}
                  />
                </label>
                <label style={styles.label}>
                  <strong>Date of Birth:</strong>
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleChange}
                    style={styles.input}
                  />
                </label>
                <label style={styles.label}>
                  <strong>Gender:</strong>
                  <input
                    type="text"
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                    style={styles.input}
                  />
                </label>
                <label style={styles.label}>
                  <strong>Role:</strong>
                  <input
                    type="text"
                    name="role"
                    value={formData.role}
                    onChange={handleChange}
                    style={styles.input}
                  />
                </label>
                <button style={{ ...styles.button, ...styles.saveButton }} onClick={handleSave}>
                  Save
                </button>
                <button
                  style={{ ...styles.button, ...styles.cancelButton }}
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
              </div>
            ) : (
              <div>
                <p><strong>Full Name:</strong> {personalInfo.fullName}</p>
                <p><strong>Email:</strong> {personalInfo.email}</p>
                <p><strong>Phone:</strong> {personalInfo.phone}</p>
                <p><strong>Date of Birth:</strong> {personalInfo.dob}</p>
                <p><strong>Gender:</strong> {personalInfo.gender}</p>
                <p><strong>Role:</strong> {personalInfo.role}</p>
                <button style={{ ...styles.button, ...styles.saveButton }} onClick={() => setIsEditing(true)}>
                  Edit
                </button>
              </div>
            )}
          </div>
        ) : !error ? (
          <p className="personal-info-loading">Loading...</p>
        ) : null}
      </div>
    </div>
  );
};

export default PersonalInfo;
