import React, { useEffect, useState } from "react";
import axios from "axios";

const PersonalInfo = () => {
  const [personalInfo, setPersonalInfo] = useState(null);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    dob: "",
    gender: "",
    role: "",
  });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      setError("User ID not found. Please log in again.");
      return;
    }

    axios
      .get(`http://localhost:5000/api/profile/personal-info/${userId}`)
      .then((response) => {
        const { fullName, email, phone, dob, gender, role } = response.data;
        setPersonalInfo(response.data);
        setFormData({
          fullName,
          email,
          phone,
          dob: dob || "",
          gender,
          role,
        });
      })
      .catch((error) => {
        console.error("Error fetching personal info:", error);
        setError("Failed to load personal info.");
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = () => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      alert("User ID is missing.");
      return;
    }

    axios
      .put(`http://localhost:5000/api/profile/update-personal-info/${userId}`, formData)
      .then((response) => {
        setPersonalInfo(response.data);
        setIsEditing(false);
      })
      .catch((error) => {
        console.error("Error updating personal info:", error);
        setError("Failed to update personal info.");
      });
  };

  // **ðŸŽ¨ Styled Components for Proper Alignment & Spacing**
  const styles = {
    wrapper: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      minHeight: "100vh",
      backgroundColor: "#f9fafb",
      paddingLeft: "280px", // Adjust spacing from the sidebar
      paddingRight: "40px", // Extra padding for better centering
      transition: "padding-left 0.3s ease-in-out",
    },
    container: {
      width: "550px",
      padding: "30px",
      background: "white",
      boxShadow: "0px 8px 24px rgba(0, 0, 0, 0.1)",
      borderRadius: "12px",
      textAlign: "center",
    },
    title: {
      fontSize: "24px",
      fontWeight: "bold",
      marginBottom: "20px",
      color: "#333",
    },
    errorText: {
      color: "red",
      fontSize: "14px",
      marginBottom: "10px",
    },
    inputGroup: {
      marginBottom: "15px",
      textAlign: "left",
    },
    label: {
      display: "block",
      fontWeight: "bold",
      color: "#555",
      marginBottom: "5px",
    },
    input: {
      width: "100%",
      padding: "10px",
      border: "1px solid #ddd",
      borderRadius: "6px",
      fontSize: "14px",
      outline: "none",
      transition: "border 0.3s ease-in-out",
    },
    buttonGroup: {
      display: "flex",
      justifyContent: "space-between",
      marginTop: "20px",
    },
    button: {
      flex: "1",
      padding: "12px",
      fontSize: "14px",
      fontWeight: "bold",
      border: "none",
      borderRadius: "6px",
      cursor: "pointer",
      transition: "0.3s",
      margin: "0 5px",
    },
    saveButton: {
      backgroundColor: "#007bff",
      color: "white",
    },
    cancelButton: {
      backgroundColor: "#dc3545",
      color: "white",
    },
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.container}>
        <h2 style={styles.title}>Personal Information</h2>
        {error && <p style={styles.errorText}>{error}</p>}

        {personalInfo ? (
          <div>
            {isEditing ? (
              <div>
                {["fullName", "email", "phone", "dob", "gender", "role"].map((field) => (
                  <div key={field} style={styles.inputGroup}>
                    <label style={styles.label}>
                      {field.replace(/^\w/, (c) => c.toUpperCase())}:
                    </label>
                    <input
                      type={field === "dob" ? "date" : "text"}
                      name={field}
                      value={formData[field]}
                      onChange={handleChange}
                      style={styles.input}
                    />
                  </div>
                ))}
                <div style={styles.buttonGroup}>
                  <button style={{ ...styles.button, ...styles.saveButton }} onClick={handleSave}>
                    Save
                  </button>
                  <button
                    style={{ ...styles.button, ...styles.cancelButton }}
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            ) : (
              <div>
                <p><strong>Full Name:</strong> {personalInfo.fullName}</p>
                <p><strong>Email:</strong> {personalInfo.email}</p>
                <p><strong>Phone:</strong> {personalInfo.phone}</p>
                <p><strong>Date of Birth:</strong> {personalInfo.dob}</p>
                <p><strong>Gender:</strong> {personalInfo.gender}</p>
                <p><strong>Role:</strong> {personalInfo.role}</p>
                <button
                  style={{ ...styles.button, ...styles.saveButton }}
                  onClick={() => setIsEditing(true)}
                >
                  Edit
                </button>
              </div>
            )}
          </div>
        ) : !error ? (
          <p className="personal-info-loading">Loading...</p>
        ) : null}
      </div>
    </div>
  );
};

export default PersonalInfo;
