import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const OngoingAssessments = () => {
  const [assessments, setAssessments] = useState([]);
  const [filteredAssessments, setFilteredAssessments] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState("all");
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); // Fetch userId from localStorage

  useEffect(() => {
    const fetchAssessments = async () => {
      try {
        const token = localStorage.getItem("token");

        // ✅ Fetch ongoing assessments for enrolled courses
        const assessmentsRes = await axios.get(`http://localhost:5000/api/assessments/ongoing/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAssessments(assessmentsRes.data);
        setFilteredAssessments(assessmentsRes.data);

        // ✅ Fetch enrolled courses
        const coursesRes = await axios.get(`http://localhost:5000/api/assessments/enrolled-courses/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCourses(coursesRes.data);
      } catch (err) {
        console.error("Error fetching data", err);
      }
    };

    fetchAssessments();
  }, [userId]);

  // Filter assessments based on selected course
  useEffect(() => {
    if (selectedCourse === "all") {
      setFilteredAssessments(assessments);
    } else {
      setFilteredAssessments(assessments.filter(a => a.courseId === selectedCourse));
    }
  }, [selectedCourse, assessments]);

  return (
    <div className="container mt-5">
      <h1 className="text-center">📝 Your Ongoing Assessments</h1>

      {/* Course Filter Dropdown */}
      <div className="text-center mb-4">
        <label className="me-2 fw-bold">Filter by Course:</label>
        <select className="form-select w-auto d-inline-block"
          value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)}>
          <option value="all">All Courses</option>
          {courses.map(course => (
            <option key={course._id} value={course._id}>{course.name}</option>
          ))}
        </select>
      </div>

      {/* Assessments Display */}
      {filteredAssessments.length === 0 ? (
        <p className="text-center">No active assessments in this course.</p>
      ) : (
        <div className="row">
          {filteredAssessments.map(assessment => (
            <div key={assessment._id} className="col-md-6 mb-4">
              <div className="card p-3 shadow">
                <h5>{assessment.title}</h5>
                <p>{assessment.description}</p>
                <button className="btn btn-success"
                  onClick={() => navigate(`/assessments/${assessment._id}`)}>
                  Attempt Now
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <button className="btn btn-secondary mt-3" onClick={() => navigate("/assessments")}>
        🔙 Back
      </button>
    </div>
  );
};

export default OngoingAssessments;
