import React, { useEffect, useState } from "react";
import axios from "axios";

const CreateAssessment = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState("");
  const [questions, setQuestions] = useState([]);
  const [assessments, setAssessments] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId === "admin") {
      setIsAdmin(true);
    }

    const fetchCourses = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/courses");
        setCourses(response.data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    const fetchAssessments = async () => {
      try {
        const response = await axios.get(
          isAdmin
            ? "http://localhost:5000/api/assessments/all"
            : `http://localhost:5000/api/assessments/enrolled/${userId}`
        );
        setAssessments(response.data);
      } catch (error) {
        console.error("Error fetching assessments:", error);
      }
    };

    fetchCourses();
    fetchAssessments();
  }, [isAdmin]);

  const handleAddQuestion = () => {
    setQuestions([
      ...questions,
      { questionText: "", options: ["", "", "", ""], correctAnswer: "" },
    ]);
  };

  const handleChangeQuestion = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].questionText = value;
    setQuestions(newQuestions);
  };

  const handleChangeOption = (qIndex, optIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[optIndex] = value;
    setQuestions(newQuestions);
  };

  const handleSelectCorrectAnswer = (qIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].correctAnswer = value;
    setQuestions(newQuestions);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userId = localStorage.getItem("userId");

    if (!userId || !selectedCourse) {
      alert("Please select a course and ensure you are logged in as an admin.");
      return;
    }

    const requestData = {
      userId,
      title,
      description,
      courseId: selectedCourse,
      dueDate,
      questions,
    };

    console.log("Sending request data:", requestData);

    try {
      await axios.post(
        "http://localhost:5000/api/assessments/create",
        requestData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      alert("✅ Assessment Created Successfully!");
    } catch (error) {
      console.error("Error creating assessment", error);
      alert("❌ Failed to create assessment.");
    }
  };

  return (
    <section
      style={{
        marginLeft: "250px",
        width: "calc(100% - 250px)",
        padding: "30px",
        minHeight: "100vh",
        backgroundColor: "#f8f9fa",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <div
        style={{
          background: "#ffffff",
          padding: "30px",
          borderRadius: "12px",
          width: "500px",
          boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
          marginBottom: "30px",
        }}
      >
        <h2
          style={{
            fontSize: "24px",
            fontWeight: "700",
            color: "#003c8f",
            textAlign: "center",
          }}
        >
          📝 Assessments
        </h2>
        {assessments.map((assessment) => (
          <div key={assessment._id} style={{ marginBottom: "10px", padding: "10px", border: "1px solid #ddd", borderRadius: "8px" }}>
            <h3>{assessment.title}</h3>
            <p>{assessment.description}</p>
            <p><strong>Due Date:</strong> {new Date(assessment.dueDate).toLocaleDateString()}</p>
          </div>
        ))}
      </div>

      {isAdmin && (
        <div
          style={{
            background: "#ffffff",
            padding: "30px",
            borderRadius: "12px",
            width: "500px",
            boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
          }}
        >
          <h2
            style={{
              fontSize: "24px",
              fontWeight: "700",
              color: "#003c8f",
              textAlign: "center",
            }}
          >
            📝 Create Assessment
          </h2>
          <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            <label>Title:</label>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />

            <label>Description:</label>
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} required rows="3" />

            <label>Due Date:</label>
            <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} required />

            <label>Select Course:</label>
            <select value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)} required>
              <option value="">-- Select a Course --</option>
              {courses.map((course) => (
                <option key={course._id} value={course._id}>{course.title}</option>
              ))}
            </select>

            <button type="submit">✅ Create Assessment</button>
          </form>
        </div>
      )}
    </section>
  );
};

export default CreateAssessment;
