import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../courses.css"; // Importing the new CSS file

const Courses = ({ role }) => {
    const navigate = useNavigate();
    const [coursesData, setCoursesData] = useState([]); 
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch("http://localhost:5000/api/courses") 
            .then((res) => res.json())
            .then((data) => {
                setCoursesData(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching courses:", err);
                setLoading(false);
            });
    }, []);

    return (
        <section className="courses-container"> {/* Added Sidebar Fix Class */}
            <div className="courses-header">
                <h2>üìö Available Courses</h2>
                {role === "admin" && (
                    <button 
                        className="add-course-btn"
                        onClick={() => navigate("/create-course")}
                    >
                        ‚ûï Add Course
                    </button>
                )}
            </div>

            {loading ? (
                <p className="loading-message">‚è≥ Loading courses...</p>
            ) : coursesData.length > 0 ? (
                <div className="courses-grid">
                    {coursesData.map((course, index) => (
                        <div key={index} className="course-card">
                            <img src={course.img} alt={course.title} />
                            <h3>{course.title}</h3>
                            <p>{course.desc}</p>
                            <p><strong>Price:</strong> {course.price}</p>
                            <div className="btn-container">
                                <button 
                                    className="enroll-btn"
                                    onClick={() => navigate(`/courses/${index}`)}
                                >
                                    Enroll Now
                                </button>
                                {role === "admin" && (
                                    <button 
                                        className="manage-btn"
                                        onClick={() => navigate(`/edit-course/${index}`)}
                                    >
                                        ‚öôÔ∏è Manage
                                    </button>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="no-courses">
                    <p>üòî No courses available at the moment.</p>
                    {role === "admin" && (
                        <button 
                            className="add-course-btn"
                            onClick={() => navigate("/create-course")}
                        >
                            ‚ûï Add a Course
                        </button>
                    )}
                </div>
            )}
        </section>
    );
};

export default Courses;
