import React from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";

const Profile = () => {
  const navigate = useNavigate();

  const role = localStorage.getItem("role"); // Get user role from localStorage

  const profileData = [
    { title: "ðŸ‘¤ Personal Info", desc: "View and edit your personal details.", path: "/profile/personal-info" },
    { title: "ðŸ“š Enrolled Courses", desc: "Check the list of your active courses.", path: "/enrolled-courses/:userId" },
    { title: "ðŸ“Š Progress Report", desc: "Monitor your learning progress and achievements.", path: "/assessments/analytics" },
    // { title: "ðŸ”’ Change Password", desc: "Update your account security settings.", path: "/profile/change-password" },
  ];

  // Show only Personal Info if admin
  const filteredProfileData = role === "admin"
    ? profileData.filter((item) => item.title.includes("Personal Info"))
    : profileData;

  return (
    <section className="container mt-5">
      <h1 className="text-center">Profile</h1>
      <div className="row">
        {filteredProfileData.map((data, index) => (
          <div key={index} className="col-lg-4 col-md-6 mb-4">
            <div className="card h-100 shadow-sm">
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{data.title}</h5>
                <p className="card-text flex-grow-1">{data.desc}</p>
                <button className="btn btn-primary mt-auto" onClick={() => navigate(data.path)}>
                  Manage
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Profile;
