import React, { useEffect, useState } from "react";
import axios from "axios";

const CreateAssessment = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState("");
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/courses");
        setCourses(response.data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    fetchCourses();
  }, []);

  const handleAddQuestion = () => {
    setQuestions([...questions, { questionText: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleChangeQuestion = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].questionText = value;
    setQuestions(newQuestions);
  };

  const handleChangeOption = (qIndex, optIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[optIndex] = value;
    setQuestions(newQuestions);
  };

  const handleSelectCorrectAnswer = (qIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].correctAnswer = value;
    setQuestions(newQuestions);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userId = localStorage.getItem("userId");

    if (!userId || !selectedCourse) {
      alert("Please select a course and ensure you are logged in as an admin.");
      return;
    }

    const requestData = {
      userId,
      title,
      description,
      courseId: selectedCourse,
      dueDate,
      questions
    };

    console.log("Sending request data:", requestData); // Debugging log

    try {
      const response = await axios.post(
        "http://localhost:5000/api/assessments/create",
        requestData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      alert("‚úÖ Assessment Created Successfully!");
    } catch (error) {
      console.error("Error creating assessment", error);
      alert("‚ùå Failed to create assessment.");
    }
  };


  return (
    <section style={{ marginLeft: "250px", width: "calc(100% - 250px)", padding: "30px", minHeight: "100vh", backgroundColor: "#f8f9fa", display: "flex", justifyContent: "center", alignItems: "center" }}>
      <div style={{ background: "#ffffff", padding: "30px", borderRadius: "12px", width: "500px", boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)" }}>
        <h2 style={{ fontSize: "24px", fontWeight: "700", color: "#003c8f", textAlign: "center" }}>üìù Create Assessment</h2>
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
          <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "8px", fontSize: "16px" }} />

          <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required rows="3" style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "8px", fontSize: "16px", resize: "none" }} />

          <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Due Date:</label>
          <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} required style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "8px", fontSize: "16px" }} />

          <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Select Course:</label>
          <select value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)} required style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "8px", fontSize: "16px", background: "white" }}>
            <option value="">-- Select a Course --</option>
            {courses.map((course) => (
              <option key={course._id} value={course._id}>
                {course.title}
              </option>
            ))}
          </select>

          {questions.map((q, qIndex) => (
            <div key={qIndex} style={{ background: "#f8f9fa", padding: "15px", borderRadius: "10px" }}>
              <label style={{ fontSize: "16px", fontWeight: "bold", color: "#555" }}>Question {qIndex + 1}:</label>
              <input type="text" value={q.questionText} onChange={(e) => handleChangeQuestion(qIndex, e.target.value)} required style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "8px", fontSize: "16px" }} />

              {q.options.map((opt, optIndex) => (
                <input key={optIndex} type="text" placeholder={`Option ${optIndex + 1}`} value={opt} onChange={(e) => handleChangeOption(qIndex, optIndex, e.target.value)} required style={{ padding: "10px", border: "1px solid #ccc", borderRadius: "8px", fontSize: "16px", marginTop: "5px" }} />
              ))}
            </div>
          ))}

          <button type="button" onClick={handleAddQuestion}>‚ûï Add Question</button>
          <button type="submit">‚úÖ Create Assessment</button>
        </form>
      </div>
    </section>
  );
};

export default CreateAssessment;
