import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../App.css";

const Dashboard = () => {
    const navigate = useNavigate();
    const role = localStorage.getItem("role");
    const userId = localStorage.getItem("userId");

    const [users, setUsers] = useState([]);
    const [assessments, setAssessments] = useState([]);

    useEffect(() => {
        if (role === "admin") {
            // Fetch all users
            axios.get("/api/users")
                .then(res => setUsers(res.data))
                .catch(err => console.error("Failed to fetch users", err));

            // Fetch all assessments
            axios.get("/api/assessments")
                .then(res => setAssessments(res.data))
                .catch(err => console.error("Failed to fetch assessments", err));
        }
    }, [role]);

    // Student dashboard cards
    const studentItems = [
        {
            title: "üìö My Courses",
            desc: "View and manage your enrolled courses.",
            path: `/enrolled-courses/${userId}`,
        },
        {
            title: "üìù Assignments",
            desc: "Check and submit your assignments on time.",
            path: "/assessments",
        },
        {
            title: "üìä Progress Report",
            desc: "Track your learning progress and performance.",
            path: "/assessments/analytics",
        },
    ];

    if (role === "admin") {
        return (
            <section className="container mt-5">
                <h1 className="text-center">Admin Dashboard</h1>

                <h3 className="mt-5">üë• All Users</h3>
                <div className="table-responsive">
                    <table className="table table-bordered table-hover">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Role</th>
                                <th>Phone</th>
                                <th>DOB</th>
                                <th>Gender</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map((user) => (
                                <tr key={user._id}>
                                    <td>{user.fullName}</td>
                                    <td>{user.email}</td>
                                    <td>{user.role}</td>
                                    <td>{user.phone}</td>
                                    <td>{new Date(user.dob).toLocaleDateString()}</td>
                                    <td>{user.gender}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                <h3 className="mt-5">üìù All Assessments</h3>
                <div className="table-responsive">
                    <table className="table table-bordered table-hover">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Description</th>
                                <th>Total Questions</th>
                                <th>Created By</th>
                            </tr>
                        </thead>
                        <tbody>
                            {assessments.map((a) => (
                                <tr key={a._id}>
                                    <td>{a.title}</td>
                                    <td>{a.description}</td>
                                    <td>{a.questions.length}</td>
                                    <td>{a.createdBy?.fullName || "N/A"}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>
        );
    }

    // For students
    return (
        <section className="container mt-5">
            <h1 className="text-center">Student Dashboard</h1>
            <div className="row">
                {studentItems.map((item, index) => (
                    <div key={index} className="col-lg-4 col-md-6 mb-4">
                        <div className="card h-100 shadow-sm">
                            <div className="card-body d-flex flex-column">
                                <h5 className="card-title">{item.title}</h5>
                                <p className="card-text flex-grow-1">{item.desc}</p>
                                <button
                                    className="btn btn-primary mt-auto"
                                    onClick={() => navigate(item.path)}
                                >
                                    Go to {item.title.split(" ")[1]}
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </section>
    );
};

export default Dashboard;
