import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const OngoingAssessments = () => {
  const [assessments, setAssessments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/assessments/ongoing", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      })
      .then((res) => setAssessments(res.data))
      .catch((err) => console.error("Error fetching assessments", err));
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center">üìù Your Ongoing Assessments</h1>
      {assessments.length === 0 ? (
        <p className="text-center">No active assessments assigned to you.</p>
      ) : (
        <div className="row">
          {assessments.map((assessment) => (
            <div key={assessment._id} className="col-md-6 mb-4">
              <div className="card p-3 shadow">
                <h5>{assessment.title}</h5>
                <p>{assessment.description}</p>
                <button
                  className="btn btn-success"
                  onClick={() => navigate(`/assessments/${assessment._id}`)}
                >
                  Attempt Now
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
      <button className="btn btn-secondary mt-3" onClick={() => navigate("/assessments")}>
        üîô Back
      </button>
    </div>
  );
};

export default OngoingAssessments;
