import React, { useEffect, useState } from "react";
import axios from "axios";
import "../App.css"; // Ensure CSS is imported

const PersonalInfo = () => {
  const [personalInfo, setPersonalInfo] = useState(null);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false); // Track edit mode
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    dob: "",  // Ensure dob is initialized correctly
    gender: "",
    role: "",
  });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      setError("User ID not found. Please log in again.");
      return;
    }

    axios
      .get(`http://localhost:5000/api/profile/personal-info/${userId}`)
      .then((response) => {
        const { fullName, email, phone, dob, gender, role } = response.data;
        setPersonalInfo(response.data);
        setFormData({
          fullName,
          email,
          phone,
          dob: dob || "",  // Make sure dob is in a valid format
          gender,
          role,
        }); // Set initial form data for editing
      })
      .catch((error) => {
        console.error("Error fetching personal info:", error);
        setError("Failed to load personal info.");
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSave = () => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      alert("User ID is missing.");
      return;
    }

    axios
      .put(`http://localhost:5000/api/profile/update-personal-info/${userId}`, formData)
      .then((response) => {
        setPersonalInfo(response.data);
        setIsEditing(false);
      })
      .catch((error) => {
        console.error("Error updating personal info:", error);
        setError("Failed to update personal info.");
      });
  };


  return (
    <div className="personal-info-wrapper">
      <div className="personal-info-container">
        <h2>Personal Info</h2>
        {error && <p className="personal-info-error">{error}</p>}
        {personalInfo ? (
          <div>
            {isEditing ? (
              <div>
                <label>
                  <strong>Full Name:</strong>
                  <input
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                  />
                </label>
                <label>
                  <strong>Email:</strong>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </label>
                <label>
                  <strong>Phone:</strong>
                  <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                  />
                </label>
                <label>
                  <strong>Date of Birth:</strong>
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleChange}
                  />
                </label>
                <label>
                  <strong>Gender:</strong>
                  <input
                    type="text"
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                  />
                </label>
                <label>
                  <strong>Role:</strong>
                  <input
                    type="text"
                    name="role"
                    value={formData.role}
                    onChange={handleChange}
                  />
                </label>
                <button onClick={handleSave}>Save</button>
                <button onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            ) : (
              <div>
                <p><strong>Full Name:</strong> {personalInfo.fullName}</p>
                <p><strong>Email:</strong> {personalInfo.email}</p>
                <p><strong>Phone:</strong> {personalInfo.phone}</p>
                <p><strong>Date of Birth:</strong> {personalInfo.dob}</p>
                <p><strong>Gender:</strong> {personalInfo.gender}</p>
                <p><strong>Role:</strong> {personalInfo.role}</p>
                <button onClick={() => setIsEditing(true)}>Edit</button>
              </div>
            )}
          </div>
        ) : !error ? (
          <p className="personal-info-loading">Loading...</p>
        ) : null}
      </div>
    </div>
  );
};

export default PersonalInfo;
