import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css"; // Ensure Bootstrap is included

const Courses = ({ role }) => {
    const navigate = useNavigate();
    const [coursesData, setCoursesData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch("http://localhost:5000/api/courses")
            .then((res) => res.json())
            .then((data) => {
                setCoursesData(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching courses:", err);
                setLoading(false);
            });
    }, []);

    return (
        <section className="container-fluid p-4" style={{ marginLeft: "250px", width: "calc(100% - 250px)", minHeight: "100vh" }}>
            {/* Header Section */}
            <div className="d-flex justify-content-between align-items-center bg-white p-3 shadow rounded mb-4">
                <h2 className="text-primary fw-bold m-0">📚 Available Courses</h2>
                {role === "admin" && (
                    <button className="btn btn-success fw-bold" onClick={() => navigate("/create-course")}>
                        ➕ Add Course
                    </button>
                )}
            </div>

            {/* Course Cards */}
            {loading ? (
                <p className="text-center text-secondary fs-5">⏳ Loading courses...</p>
            ) : coursesData.length > 0 ? (
                <div className="row">
                    {coursesData.map((course, index) => (
                        <div key={index} className="col-lg-4 col-md-6 mb-4">
                            <div className="card h-100 shadow">
                                <img 
                                    src={course.img} 
                                    alt={course.title} 
                                    className="card-img-top" 
                                    style={{ height: "200px", objectFit: "cover" }} 
                                />
                                <div className="card-body d-flex flex-column">
                                    <h5 className="card-title text-primary">{course.title}</h5>
                                    <p className="card-text text-muted flex-grow-1">{course.desc}</p>
                                    <p className="fw-bold">Price: {course.price}</p>
                                    <div className="d-flex justify-content-between">
                                        <button className="btn btn-primary" onClick={() => navigate(`/courses/${index}`)}>
                                            Enroll Now
                                        </button>
                                        {role === "admin" && (
                                            <button className="btn btn-warning text-dark" onClick={() => navigate(`/edit-course/${index}`)}>
                                                ⚙️ Manage
                                            </button>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="text-center bg-white p-4 rounded shadow">
                    <p className="fs-5 text-muted">😔 No courses available at the moment.</p>
                    {role === "admin" && (
                        <button className="btn btn-success fw-bold mt-2" onClick={() => navigate("/create-course")}>
                            ➕ Add a Course
                        </button>
                    )}
                </div>
            )}
        </section>
    );
};

export default Courses;
