import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";

const Courses = ({ role }) => {
    const navigate = useNavigate();
    const [coursesData, setCoursesData] = useState([]); // ✅ Initial state as empty array
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulating an API call (Replace with actual API call)
        fetch("http://localhost:5000/api/courses") // Replace with your API endpoint
            .then((res) => res.json())
            .then((data) => {
                setCoursesData(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching courses:", err);
                setLoading(false);
            });
    }, []);

    return (
        <section className="container mt-5">
            <h1 className="text-center">📚 Available Courses</h1>

            {loading ? (
                <p className="text-center">⏳ Loading courses...</p>
            ) : coursesData.length > 0 ? (
                <div className="row">
                    {coursesData.map((course, index) => (
                        <div key={index} className="col-lg-4 col-md-6 mb-4">
                            <div className="card h-100 shadow-sm">
                                <img
                                    src={course.img}
                                    alt={course.title}
                                    className="card-img-top"
                                    style={{ height: "200px", objectFit: "cover" }}
                                />
                                <div className="card-body d-flex flex-column">
                                    <h5 className="card-title">{course.title}</h5>
                                    <p className="card-text flex-grow-1">{course.desc}</p>
                                    <p className="card-text"><strong>Price:</strong> {course.price}</p>
                                    <button 
                                        className="btn btn-primary mt-auto"
                                        onClick={() => navigate(`/courses/${index}`)}
                                    >
                                        Enroll Now
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="text-center mt-4">
                    <p>😔 No courses available at the moment.</p>
                    {role === "admin" && (
                        <button 
                            className="btn btn-success mt-3"
                            onClick={() => navigate("/create-course")}
                        >
                            ➕ Add a Course
                        </button>
                    )}
                </div>
            )}

            {role === "admin" && coursesData.length > 0 && (
                <div className="text-center mt-4">
                    <button 
                        className="btn btn-success"
                        onClick={() => navigate("/create-course")}
                    >
                        ➕ Create New Course
                    </button>
                </div>
            )}
        </section>
    );
};

export default Courses;
