import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CreateCourse = ({ role }) => {
    const navigate = useNavigate();

    // Redirect non-admin users
    useEffect(() => {
        if (role !== "admin") {
            navigate("/");
        }
    }, [role, navigate]);

    const [course, setCourse] = useState({
        title: "",
        desc: "",
        price: "",
        img: "", // Store image URL instead of file
    });

    const handleChange = (e) => {
        setCourse({ ...course, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!course.img.startsWith("http")) {
            alert("Please enter a valid image URL");
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/courses", course);

            alert("Course added successfully!");
            navigate("/");
        } catch (error) {
            console.error("Error adding course:", error);
        }
    };

    return (
        <div className="container mt-5">
            <h2>Add New Course</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Title</label>
                    <input type="text" className="form-control" name="title" value={course.title} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Description</label>
                    <textarea className="form-control" name="desc" value={course.desc} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Price</label>
                    <input type="number" className="form-control" name="price" value={course.price} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Image URL</label>
                    <input type="text" className="form-control" name="img" value={course.img} onChange={handleChange} required />
                    <small className="text-muted">Provide a valid image URL (e.g., https://example.com/image.jpg)</small>
                </div>
                <button type="submit" className="btn btn-primary">Add Course</button>
            </form>
        </div>
    );
};

export default CreateCourse;
