import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Courses = ({ role }) => {
    const navigate = useNavigate();
    const [coursesData, setCoursesData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        console.log("User Role:", role); // Debugging Role
        fetch("http://localhost:5000/api/courses")
            .then((res) => res.json())
            .then((data) => {
                setCoursesData(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching courses:", err);
                setLoading(false);
            });
    }, []);

    return (
        <section className="container mt-5" style={{ marginLeft: "250px", width: "calc(100% - 250px)" }}>
            {/* Header */}
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2 className="text-primary fw-bold">📚 Available Courses</h2>
                {role === "admin" && (
                    <button 
                        className="btn btn-success fw-bold"
                        onClick={() => navigate("/create-course")}
                    >
                        ➕ Add Course
                    </button>
                )}
            </div>

            {/* Courses List */}
            {loading ? (
                <p className="text-center fs-5 text-muted">⏳ Loading courses...</p>
            ) : coursesData.length > 0 ? (
                <div className="row">
                    {coursesData.map((course, index) => (
                        <div key={index} className="col-lg-4 col-md-6 mb-4">
                            <div className="card h-100 shadow-sm">
                                <img 
                                    src={course.img} 
                                    className="card-img-top"
                                    alt={course.title}
                                    style={{ height: "200px", objectFit: "cover" }}
                                />
                                <div className="card-body d-flex flex-column">
                                    <h5 className="card-title">{course.title}</h5>
                                    <p className="card-text flex-grow-1">{course.desc}</p>
                                    <p className="card-text"><strong>Price:</strong> {course.price}</p>
                                    <div className="d-flex justify-content-between">
                                        <button 
                                            className="btn btn-primary"
                                            onClick={() => navigate(`/courses/${index}`)}
                                        >
                                            Enroll Now
                                        </button>
                                        {role === "admin" && (
                                            <button 
                                                className="btn btn-warning"
                                                onClick={() => navigate(`/edit-course/${index}`)}
                                            >
                                                ⚙️ Manage
                                            </button>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="text-center p-4 bg-white shadow-sm rounded">
                    <p className="fs-5 text-muted">😔 No courses available at the moment.</p>
                    {role === "admin" && (
                        <button 
                            className="btn btn-success fw-bold"
                            onClick={() => navigate("/create-course")}
                        >
                            ➕ Add a Course
                        </button>
                    )}
                </div>
            )}
        </section>
    );
};

export default Courses;
