import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const EditAssessment = () => {
  const { assessmentId } = useParams();
  const navigate = useNavigate();
  const [assessment, setAssessment] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    const fetchAssessment = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/assessments/${assessmentId}`);
        const data = response.data;
        setAssessment(data);
        setTitle(data.title);
        setDescription(data.description);
        setDueDate(data.dueDate);
        setQuestions(data.questions);
      } catch (error) {
        console.error("Error fetching assessment:", error);
      }
    };
    fetchAssessment();
  }, [assessmentId]);

  const handleQuestionChange = (qIndex, field, value) => {
    setQuestions((prevQuestions) => {
      const updatedQuestions = [...prevQuestions];
      updatedQuestions[qIndex] = { ...updatedQuestions[qIndex], [field]: value };
      return updatedQuestions;
    });
  };

  const handleOptionChange = (qIndex, optIndex, value) => {
    setQuestions((prevQuestions) => {
      const updatedQuestions = [...prevQuestions];
      updatedQuestions[qIndex].options[optIndex] = value;
      return updatedQuestions;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/assessments/${assessmentId}`, {
        title,
        description,
        dueDate,
        questions,
      });
      alert("✅ Assessment updated successfully!");
      navigate(-1);
    } catch (error) {
      console.error("Error updating assessment:", error);
      alert("❌ Failed to update assessment.");
    }
  };

  return (
    <div className="edit-assessment-container">
      <h2 className="heading">Edit Assessment</h2>
      <form onSubmit={handleSubmit} className="edit-assessment-form">
        <label>Title:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />

        <label>Description:</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />

        <label>Due Date:</label>
        <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} required />

        {questions.map((q, qIndex) => (
          <div key={qIndex} className="question-container">
            <label>Question {qIndex + 1}:</label>
            <input type="text" value={q.questionText} onChange={(e) => handleQuestionChange(qIndex, "questionText", e.target.value)} required />

            {q.options.map((option, optIndex) => (
              <input key={optIndex} type="text" value={option} onChange={(e) => handleOptionChange(qIndex, optIndex, e.target.value)} required />
            ))}

            <label>Correct Answer:</label>
            <input type="text" value={q.correctAnswer} onChange={(e) => handleQuestionChange(qIndex, "correctAnswer", e.target.value)} required />
          </div>
        ))}

        <div className="button-group">
          <button type="submit" className="save-button">Save Changes</button>
          <button type="button" className="cancel-button" onClick={() => navigate(-1)}>Cancel</button>
        </div>
      </form>

      {/* Inline CSS */}
      <style>
        {`
          .edit-assessment-container {
            max-width: 600px;
            margin: 0 auto;
            padding: 20px;
            background: #fff;
            border-radius: 8px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          }

          .heading {
            text-align: center;
            margin-bottom: 20px;
            color: #333;
          }

          .edit-assessment-form {
            display: flex;
            flex-direction: column;
          }

          label {
            font-weight: bold;
            margin-top: 10px;
          }

          input, textarea {
            width: 100%;
            padding: 8px;
            margin-top: 5px;
            border: 1px solid #ccc;
            border-radius: 4px;
          }

          .question-container {
            margin-top: 15px;
            padding: 10px;
            background: #f9f9f9;
            border-radius: 6px;
          }

          .button-group {
            display: flex;
            justify-content: space-between;
            margin-top: 20px;
          }

          .save-button {
            background-color: #28a745;
            color: white;
            border: none;
            padding: 10px 15px;
            border-radius: 5px;
            cursor: pointer;
          }

          .save-button:hover {
            background-color: #218838;
          }

          .cancel-button {
            background-color: #dc3545;
            color: white;
            border: none;
            padding: 10px 15px;
            border-radius: 5px;
            cursor: pointer;
          }

          .cancel-button:hover {
            background-color: #c82333;
          }
        `}
      </style>
    </div>
  );
};

export default EditAssessment;
