import React, { useEffect, useState } from "react";
import axios from "axios";

const CreateAssessment = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState("");
  const [questions, setQuestions] = useState([]);
  const [assessments, setAssessments] = useState([]);
  const [role, setRole] = useState("");
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/courses");
        setCourses(response.data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    const fetchUserRole = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setRole(response.data.role);
      } catch (error) {
        console.error("Error fetching user role:", error);
      }
    };

    const fetchAssessments = async () => {
      try {
        const url = role === "admin"
          ? "http://localhost:5000/api/assessments"
          : `http://localhost:5000/api/assessments/student/${userId}`;
        const response = await axios.get(url);
        setAssessments(response.data);
      } catch (error) {
        console.error("Error fetching assessments:", error);
      }
    };

    fetchCourses();
    fetchUserRole();
    fetchAssessments();
  }, [role, userId]);

  const handleAddQuestion = () => {
    setQuestions([
      ...questions,
      { questionText: "", options: ["", "", "", ""], correctAnswer: "" },
    ]);
  };

  const handleChangeQuestion = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].questionText = value;
    setQuestions(newQuestions);
  };

  const handleChangeOption = (qIndex, optIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[optIndex] = value;
    setQuestions(newQuestions);
  };

  const handleSelectCorrectAnswer = (qIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].correctAnswer = value;
    setQuestions(newQuestions);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId || !selectedCourse) {
      alert("Please select a course and ensure you are logged in as an admin.");
      return;
    }

    const requestData = {
      userId,
      title,
      description,
      courseId: selectedCourse,
      dueDate,
      questions,
    };

    try {
      await axios.post(
        "http://localhost:5000/api/assessments/create",
        requestData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      alert("✅ Assessment Created Successfully!");
    } catch (error) {
      console.error("Error creating assessment", error);
      alert("❌ Failed to create assessment.");
    }
  };

  return (
    <section style={{ padding: "30px", minHeight: "100vh", backgroundColor: "#f8f9fa" }}>
      <h2 style={{ textAlign: "center", color: "#003c8f" }}>📝 Assessments</h2>
      {assessments.map((assessment) => (
        <div key={assessment._id} style={{ background: "#fff", padding: "15px", borderRadius: "10px", margin: "10px 0" }}>
          <h3>{assessment.title}</h3>
          <p>{assessment.description}</p>
          <p><strong>Due Date:</strong> {new Date(assessment.dueDate).toLocaleDateString()}</p>
        </div>
      ))}

      {role === "admin" && (
        <div style={{ marginTop: "30px" }}>
          <h2 style={{ textAlign: "center", color: "#003c8f" }}>📝 Create Assessment</h2>
          <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            <label>Title:</label>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />

            <label>Description:</label>
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />

            <label>Due Date:</label>
            <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} required />

            <label>Select Course:</label>
            <select value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)} required>
              <option value="">-- Select a Course --</option>
              {courses.map((course) => (
                <option key={course._id} value={course._id}>{course.title}</option>
              ))}
            </select>

            {questions.map((q, qIndex) => (
              <div key={qIndex}>
                <label>Question {qIndex + 1}:</label>
                <input type="text" value={q.questionText} onChange={(e) => handleChangeQuestion(qIndex, e.target.value)} required />
                {q.options.map((opt, optIndex) => (
                  <div key={optIndex}>
                    <input type="text" placeholder={`Option ${optIndex + 1}`} value={opt} onChange={(e) => handleChangeOption(qIndex, optIndex, e.target.value)} required />
                    <input type="radio" name={`correct-answer-${qIndex}`} checked={q.correctAnswer === opt} onChange={() => handleSelectCorrectAnswer(qIndex, opt)} />
                  </div>
                ))}
              </div>
            ))}

            <button type="button" onClick={handleAddQuestion}>➕ Add Question</button>
            <button type="submit">✅ Create Assessment</button>
          </form>
        </div>
      )}
    </section>
  );
};

export default CreateAssessment;
