import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Courses = () => {
    const navigate = useNavigate();
    const [coursesData, setCoursesData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [role, setRole] = useState(null);
    const [checkingRole, setCheckingRole] = useState(true); // NEW: Role loading state

    useEffect(() => {
        // ‚úÖ Get role from localStorage and set it in state
        const storedRole = localStorage.getItem("role");
        console.log("Fetched Role from localStorage:", storedRole); // Debugging
        setRole(storedRole || "user");


        setCheckingRole(false); // ‚úÖ Done checking role

        // Fetch courses
        fetch("http://localhost:5000/api/courses")
            .then((res) => res.json())
            .then((data) => {
                setCoursesData(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching courses:", err);
                setLoading(false);
            });
    }, []);


    // ‚úÖ Show loading state until role is set
    if (checkingRole) {
        return <h3 style={{ textAlign: "center", marginTop: "50px" }}>üîÑ Checking access...</h3>;
    }

    return (
        <section 
            style={{
                marginLeft: "250px", // Fix for sidebar
                width: "calc(100% - 250px)",
                padding: "30px",
                minHeight: "100vh",
                backgroundColor: "#f8f9fa",
            }}
        >
            {/* Header Section */}
            <div 
                style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    marginBottom: "30px",
                    padding: "20px",
                    borderRadius: "10px",
                }}
            >
                <h2 style={{ fontSize: "28px", fontWeight: "800", color: "#003c8f", margin: "0" }}>
                    üìö Available Courses
                </h2>
                {role === "admin" && (
                    <button 
                        style={{
                            backgroundColor: "#28a745",
                            color: "white",
                            fontSize: "16px",
                            fontWeight: "bold",
                            padding: "12px 18px",
                            border: "none",
                            borderRadius: "8px",
                            cursor: "pointer",
                            transition: "all 0.3s ease",
                            display: "block"
                        }}
                        onClick={() => navigate("/create-course")}
                    >
                        ‚ûï Add Course
                    </button>
                )}
            </div>

            {/* Course Cards */}
            {loading ? (
                <p style={{ textAlign: "center", fontSize: "18px", color: "#555" }}>‚è≥ Loading courses...</p>
            ) : coursesData.length > 0 ? (
                <div 
                    style={{
                        display: "flex",
                        flexWrap: "wrap",
                        gap: "20px",
                        justifyContent: "center",
                    }}
                >
                    {coursesData.map((course, index) => (
                        <div 
                            key={index} 
                            style={{
                                background: "#ffffff",
                                padding: "20px",
                                borderRadius: "12px",
                                width: "320px",
                                textAlign: "center",
                                boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
                                transition: "all 0.3s ease-in-out",
                                display: "flex",
                                flexDirection: "column",
                                alignItems: "center",
                            }}
                        >
                            <img 
                                src={course.img} 
                                alt={course.title} 
                                style={{
                                    width: "100%",
                                    height: "180px",
                                    borderRadius: "10px",
                                    objectFit: "cover",
                                    transition: "transform 0.3s ease",
                                }}
                            />
                            <h3 style={{ fontSize: "22px", color: "#003c8f", margin: "15px 0 10px" }}>{course.title}</h3>
                            <p style={{ fontSize: "14px", color: "#555", marginBottom: "15px", textAlign: "center" }}>
                                {course.desc}
                            </p>
                            <p><strong>Price:</strong> {course.price}</p>
                            <div style={{ display: "flex", justifyContent: "space-between", width: "100%" }}>
                                <button 
                                    style={{
                                        backgroundColor: "#007bff",
                                        color: "white",
                                        padding: "10px 14px",
                                        borderRadius: "6px",
                                        fontSize: "14px",
                                        fontWeight: "bold",
                                        cursor: "pointer",
                                        border: "none",
                                        transition: "all 0.3s ease",
                                    }}
                                    onClick={() => navigate(`/courses/${index}`)}
                                >
                                    Enroll Now
                                </button>
                                {role === "admin" && (
                                    <button 
                                        style={{
                                            backgroundColor: "#ffc107",
                                            color: "black",
                                            padding: "10px 14px",
                                            borderRadius: "6px",
                                            fontSize: "14px",
                                            fontWeight: "bold",
                                            cursor: "pointer",
                                            border: "none",
                                            transition: "all 0.3s ease",
                                        }}
                                        onClick={() => navigate(`/edit-course/${index}`)}
                                    >
                                        ‚öôÔ∏è Manage
                                    </button>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div 
                    style={{
                        textAlign: "center",
                        fontSize: "18px",
                        color: "#666",
                        padding: "40px",
                        background: "#ffffff",
                        borderRadius: "10px",
                        boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
                    }}
                >
                    <p>üòî No courses available at the moment.</p>
                    {role === "admin" && (
                        <button 
                            style={{
                                backgroundColor: "#28a745",
                                color: "white",
                                fontSize: "16px",
                                fontWeight: "bold",
                                padding: "12px 18px",
                                border: "none",
                                borderRadius: "8px",
                                cursor: "pointer",
                                transition: "all 0.3s ease",
                            }}
                            onClick={() => navigate("/create-course")}
                        >
                            ‚ûï Add a Course
                        </button>
                    )}
                </div>
            )}
        </section>
    );
};

export default Courses;
