import React, { useState, useEffect } from "react";
import axios from "axios";

const GeneralDiscussions = () => {
    const [discussions, setDiscussions] = useState([]);
    const [newDiscussion, setNewDiscussion] = useState({ title: "", content: "" });
    const [selectedDiscussion, setSelectedDiscussion] = useState(null);
    const [newReply, setNewReply] = useState("");

    useEffect(() => {
        fetchDiscussions();
    }, []);

    const fetchDiscussions = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/discussions");
            setDiscussions(res.data);
        } catch (error) {
            console.error("Error fetching discussions:", error);
        }
    };

    const handleCreateDiscussion = async () => {
        if (!newDiscussion.title || !newDiscussion.content) return alert("Please enter all fields");
        try {
            await axios.post("http://localhost:5000/api/discussions", { ...newDiscussion, author: "User123" });
            setNewDiscussion({ title: "", content: "" });
            fetchDiscussions();
        } catch (error) {
            console.error("Error creating discussion:", error);
        }
    };

    const handleReply = async (discussionId) => {
        if (!newReply) return alert("Enter a reply");
        try {
            await axios.post("http://localhost:5000/api/replies", { content: newReply, author: "User123", discussion: discussionId });
            setNewReply("");
            fetchDiscussions();
        } catch (error) {
            console.error("Error adding reply:", error);
        }
    };

    const handleLike = async (discussionId) => {
        try {
            await axios.post("http://localhost:5000/api/likes", { user: "User123", targetId: discussionId, targetType: "discussion" });
            fetchDiscussions();
        } catch (error) {
            console.error("Error liking discussion:", error);
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>💬 General Discussions</h1>

            {/* Create New Discussion */}
            <div style={styles.form}>
                <input
                    type="text"
                    placeholder="Discussion Title"
                    value={newDiscussion.title}
                    onChange={(e) => setNewDiscussion({ ...newDiscussion, title: e.target.value })}
                    style={styles.input}
                />
                <textarea
                    placeholder="Discussion Content"
                    value={newDiscussion.content}
                    onChange={(e) => setNewDiscussion({ ...newDiscussion, content: e.target.value })}
                    style={styles.textarea}
                />
                <button onClick={handleCreateDiscussion} style={styles.button}>Start Discussion</button>
            </div>

            {/* Display Discussions */}
            <div>
                {discussions.map((discussion) => (
                    <div key={discussion._id} style={styles.discussionCard}>
                        <h3>{discussion.title}</h3>
                        <p>{discussion.content}</p>
                        <div style={styles.actions}>
                            <button onClick={() => handleLike(discussion._id)} style={styles.likeButton}>👍 {discussion.likes?.length || 0}</button>
                            <button onClick={() => setSelectedDiscussion(discussion._id)} style={styles.replyButton}>💬 Reply</button>
                        </div>

                        {/* Replies Section */}
                        {selectedDiscussion === discussion._id && (
                            <div style={styles.replySection}>
                                {discussion.replies?.map((reply, index) => (
                                    <p key={index} style={styles.reply}>{reply.content}</p>
                                ))}
                                <textarea
                                    placeholder="Write a reply..."
                                    value={newReply}
                                    onChange={(e) => setNewReply(e.target.value)}
                                    style={styles.textarea}
                                />
                                <button onClick={() => handleReply(discussion._id)} style={styles.button}>Reply</button>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};
// Inline CSS Styles
const styles = {
    container: {
      marginLeft: "250px",
      width: "calc(100% - 250px)",
      padding: "30px",
      minHeight: "100vh",
      backgroundColor: "#f8f9fa",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    card: {
      background: "#ffffff",
      padding: "30px",
      borderRadius: "12px",
      boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
      width: "600px",
    },
    heading: {
      textAlign: "center",
      fontSize: "24px",
      fontWeight: "700",
      color: "#003c8f",
    },
    discussionList: {
      listStyle: "none",
      padding: "0",
    },
    discussionItem: {
      padding: "15px",
      background: "#f8f9fa",
      borderRadius: "8px",
      marginBottom: "15px",
      border: "1px solid #ddd",
    },
    userInfo: {
      fontSize: "14px",
      fontWeight: "bold",
      color: "#003c8f",
    },
    discussionText: {
      fontSize: "16px",
      margin: "10px 0",
      color: "#333",
    },
    replySection: {
      marginTop: "10px",
    },
    replyInput: {
      width: "100%",
      padding: "10px",
      border: "1px solid #ccc",
      borderRadius: "8px",
      fontSize: "14px",
    },
    replyButton: {
      padding: "8px 12px",
      border: "none",
      borderRadius: "8px",
      background: "#003c8f",
      color: "white",
      cursor: "pointer",
      marginTop: "5px",
    },
    likeButton: {
      padding: "6px 10px",
      border: "none",
      borderRadius: "8px",
      background: "#28a745",
      color: "white",
      cursor: "pointer",
      marginRight: "10px",
    },
    deleteButton: {
      padding: "6px 10px",
      border: "none",
      borderRadius: "8px",
      background: "#dc3545",
      color: "white",
      cursor: "pointer",
    },
    addDiscussionButton: {
      padding: "10px",
      border: "none",
      borderRadius: "8px",
      background: "#003c8f",
      color: "white",
      cursor: "pointer",
      marginTop: "15px",
    },
    input: {
      width: "100%",
      padding: "10px",
      border: "1px solid #ccc",
      borderRadius: "8px",
      fontSize: "16px",
      marginBottom: "10px",
    },
  };





export default GeneralDiscussions;
