import React, { useState } from "react";
import axios from "axios";

const CreateAssessment = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [questions, setQuestions] = useState([
    { questionText: "", options: ["", "", "", ""], correctAnswer: "" },
  ]);

  const handleAddQuestion = () => {
    setQuestions([...questions, { questionText: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleQuestionChange = (index, value) => {
    const newQuestions = [...questions];
    newQuestions[index].questionText = value;
    setQuestions(newQuestions);
  };

  const handleOptionChange = (qIndex, oIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[oIndex] = value;
    setQuestions(newQuestions);
  };

  const handleCorrectAnswerChange = (qIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].correctAnswer = value;
    setQuestions(newQuestions);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:5000/api/assessments/create",
        { title, description, questions },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("Assessment Created Successfully");
    } catch (error) {
      console.error("Error creating assessment", error);
      alert("Failed to create assessment");
    }
  };

  return (
    <div className="container mt-5">
      <h2>Create Assessment</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" className="form-control mb-3" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea className="form-control mb-3" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />

        {questions.map((q, i) => (
          <div key={i} className="mb-3 p-3 border rounded">
            <input type="text" className="form-control mb-2" placeholder="Question" value={q.questionText} onChange={(e) => handleQuestionChange(i, e.target.value)} required />
            {q.options.map((opt, j) => (
              <input key={j} type="text" className="form-control mb-2" placeholder={`Option ${j + 1}`} value={opt} onChange={(e) => handleOptionChange(i, j, e.target.value)} required />
            ))}
            <select className="form-control mb-2" value={q.correctAnswer} onChange={(e) => handleCorrectAnswerChange(i, e.target.value)} required>
              <option value="">Select Correct Answer</option>
              {q.options.map((opt, j) => (
                <option key={j} value={opt}>{opt}</option>
              ))}
            </select>
          </div>
        ))}

        <button type="button" className="btn btn-secondary" onClick={handleAddQuestion}>Add Question</button>
        <button type="submit" className="btn btn-primary mt-3">Create</button>
      </form>
    </div>
  );
};

export default CreateAssessment;
