import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import EnrolledCourses from "./EnrolledCourses"; // Import EnrolledCourses component

const Courses = () => {
    const navigate = useNavigate();
    const [coursesData, setCoursesData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [role, setRole] = useState(null);
    const [enrolledCourses, setEnrolledCourses] = useState([]); // Track enrolled courses for student

    useEffect(() => {
        const storedRole = localStorage.getItem("role");
        if (storedRole) {
            setRole(storedRole);
        }
    }, []);

    useEffect(() => {
        fetch("http://localhost:5000/api/courses")
            .then((res) => res.json())
            .then((data) => {
                setCoursesData(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching courses:", err);
                setLoading(false);
            });
    }, []);

    // Fetch enrolled courses for the logged-in student
    useEffect(() => {
        if (role === "student") {
            const studentId = localStorage.getItem("studentId");
            if (studentId) {
                fetch(`http://localhost:5000/api/enrolled-courses/${studentId}`)
                    .then((res) => res.json())
                    .then((data) => setEnrolledCourses(data))
                    .catch((err) => console.error("Error fetching enrolled courses:", err));
            }
        }
    }, [role]);

    // Handle enroll action
    const handleEnroll = async (courseId) => {
        const studentId = localStorage.getItem("studentId");
        if (!studentId) return alert("Student ID is missing.");

        try {
            const response = await fetch("http://localhost:5000/api/enroll-course", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ studentId, courseId }),
            });

            if (response.ok) {
                alert("Enrolled successfully!");
                setEnrolledCourses((prev) => [...prev, courseId]); // Add to enrolled courses state
            } else {
                alert("Error enrolling in course.");
            }
        } catch (error) {
            console.error("Error enrolling in course:", error);
        }
    };

    if (role === null) {
        return <p>Loading...</p>;
    }

    return (
        <section
            style={{
                marginLeft: "250px",
                width: "calc(100% - 250px)",
                padding: "30px",
                minHeight: "100vh",
                backgroundColor: "#f8f9fa",
            }}
        >
            <div
                style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    marginBottom: "30px",
                    padding: "20px",
                    borderRadius: "10px",
                    width: "100%",
                }}
            >
                <h2 style={{ fontSize: "28px", fontWeight: "800", color: "#003c8f", margin: "0" }}>
                    üìö Available Courses
                </h2>
            </div>

            {loading ? (
                <p style={{ textAlign: "center", fontSize: "18px", color: "#555" }}>‚è≥ Loading courses...</p>
            ) : coursesData.length > 0 ? (
                <div
                    style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
                        gap: "20px",
                        justifyContent: "center",
                    }}
                >
                    {coursesData.map((course) => (
                        <div
                            key={course._id}
                            style={{
                                background: "#ffffff",
                                padding: "20px",
                                borderRadius: "12px",
                                textAlign: "center",
                                boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
                                display: "flex",
                                flexDirection: "column",
                                alignItems: "center",
                            }}
                        >
                            <img
                                src={course.img}
                                alt={course.title}
                                style={{
                                    width: "100%",
                                    height: "180px",
                                    borderRadius: "10px",
                                    objectFit: "cover",
                                }}
                            />
                            <h3 style={{ fontSize: "22px", color: "#003c8f", margin: "15px 0 10px" }}>
                                {course.title}
                            </h3>
                            <p style={{ fontSize: "14px", color: "#555", marginBottom: "15px", textAlign: "center" }}>
                                {course.desc}
                            </p>
                            <p><strong>Price:</strong> {course.price}</p>

                            {/* Enroll button for students */}
                            {role === "student" && (
                                <button
                                    style={{
                                        backgroundColor: "#007bff",
                                        color: "white",
                                        padding: "10px 14px",
                                        borderRadius: "6px",
                                        fontSize: "14px",
                                        fontWeight: "bold",
                                        cursor: "pointer",
                                        border: "none",
                                    }}
                                    onClick={() => handleEnroll(course._id)}
                                >
                                    Enroll Now
                                </button>
                            )}
                        </div>
                    ))}
                </div>
            ) : (
                <p style={{ textAlign: "center", fontSize: "18px", color: "#666" }}>üòî No courses available.</p>
            )}
            {role === "student" && <EnrolledCourses enrolledCourses={enrolledCourses} />}
        </section>
    );
};

export default Courses;
