import React, { useState, useEffect } from "react";

const EnrolledCourses = ({ enrolledCourses =[]}) => {
    const [courses, setCourses] = useState(enrolledCourses);

    // This effect will sync the initial prop to the state
    useEffect(() => {
        setCourses(enrolledCourses);
    }, [enrolledCourses]);

    const handleUnenroll = async (courseId) => {
        const studentId = localStorage.getItem("userId");
        if (!studentId) return alert("Student ID is missing.");

        try {
            const response = await fetch("http://localhost:5000/api/unenroll-course", {
                method: "DELETE",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ studentId, courseId }),
            });

            if (response.ok) {
                alert("Unenrolled successfully!");
                // Update the state to reflect unenrollment
                setCourses(courses.filter(course => course._id !== courseId));
            } else {
                alert("Error unenrolling from course.");
            }
        } catch (error) {
            console.error("Error unenrolling from course:", error);
        }
    };

    return (
        <div
            style={{
                marginTop: "30px",
                padding: "20px",
                backgroundColor: "#ffffff",
                borderRadius: "10px",
                boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.1)",
            }}
        >
            <h3 style={{ fontSize: "22px", color: "#003c8f", margin: "0 0 15px 0" }}>ðŸ“œ Enrolled Courses</h3>

            {courses.length > 0 ? (
                <div
                    style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
                        gap: "20px",
                        justifyContent: "center",
                    }}
                >
                    {courses.map((course) => (
                        <div
                            key={course._id}
                            style={{
                                background: "#ffffff",
                                padding: "20px",
                                borderRadius: "12px",
                                textAlign: "center",
                                boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)",
                                display: "flex",
                                flexDirection: "column",
                                alignItems: "center",
                            }}
                        >
                            <img
                                src={course.img}
                                alt={course.title}
                                style={{
                                    width: "100%",
                                    height: "180px",
                                    borderRadius: "10px",
                                    objectFit: "cover",
                                }}
                            />
                            <h3 style={{ fontSize: "22px", color: "#003c8f", margin: "15px 0 10px" }}>
                                {course.title}
                            </h3>
                            <p style={{ fontSize: "14px", color: "#555", marginBottom: "15px", textAlign: "center" }}>
                                {course.desc}
                            </p>
                            <p><strong>Price:</strong> {course.price}</p>

                            {/* Unenroll button for students */}
                            <button
                                style={{
                                    backgroundColor: "#dc3545",
                                    color: "white",
                                    padding: "10px 14px",
                                    borderRadius: "6px",
                                    fontSize: "14px",
                                    fontWeight: "bold",
                                    cursor: "pointer",
                                    border: "none",
                                }}
                                onClick={() => handleUnenroll(course._id)}
                            >
                                Unenroll
                            </button>
                        </div>
                    ))}
                </div>
            ) : (
                <p style={{ textAlign: "center", fontSize: "18px", color: "#666" }}>ðŸ˜” You are not enrolled in any courses yet.</p>
            )}
        </div>
    );
};

export default EnrolledCourses;
