import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css"; // Ensure your styles are imported

const DiscussionForum = ({ role }) => {
    const navigate = useNavigate();
    const [searchQuery, setSearchQuery] = useState("");
    const [forumThreads, setForumThreads] = useState([]);
    const [filteredThreads, setFilteredThreads] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/threads")
            .then(response => response.json())
            .then(data => {
                setForumThreads(data);
                setFilteredThreads(data);
            })
            .catch(err => console.error("Error fetching threads:", err));
    }, []);

    const handleSearch = (e) => {
        const query = e.target.value.toLowerCase();
        setSearchQuery(query);
        setFilteredThreads(forumThreads.filter(thread =>
            thread.title.toLowerCase().includes(query) ||
            thread.tags.some(tag => tag.toLowerCase().includes(query))
        ));
    };

    const handleUpvote = (id) => {
        fetch(`http://localhost:5000/api/threads/${id}/upvote`, { method: "POST" })
            .then(response => response.json())
            .then(updatedThread => {
                setForumThreads(prevThreads => prevThreads.map(thread =>
                    thread._id === id ? updatedThread : thread
                ));
                setFilteredThreads(prevThreads => prevThreads.map(thread =>
                    thread._id === id ? updatedThread : thread
                ));
            })
            .catch(err => console.error("Error upvoting:", err));
    };

    return (
        <section className="forum-container">
            <h1 className="forum-title">Discussion Forum</h1>
            <input 
                type="text" 
                placeholder="Search discussions..." 
                className="forum-search" 
                value={searchQuery} 
                onChange={handleSearch} 
            />
            <div className="forum-threads">
                {filteredThreads.length > 0 ? (
                    filteredThreads.map((thread) => (
                        <div key={thread._id} className="forum-card">
                            <h5 className="forum-thread-title">{thread.title}</h5>
                            <p className="forum-thread-description">{thread.description}</p>
                            <div className="forum-tags">
                                {thread.tags.map((tag, i) => (
                                    <span key={i} className="forum-tag">#{tag}</span>
                                ))}
                            </div>
                            <button 
                                className="forum-button" 
                                onClick={() => navigate(`/forum/${thread._id}`)}
                            >
                                View Discussion
                            </button>
                            <button 
                                className="forum-upvote" 
                                onClick={() => handleUpvote(thread._id)}
                            >
                                üëç {thread.upvotes}
                            </button>
                        </div>
                    ))
                ) : (
                    <p style={{ textAlign: "center", color: "gray", fontSize: "18px" }}>No discussions found.</p>
                )}
            </div>
        </section>
    );
};

export default DiscussionForum;
