import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";

const DiscussionForum = ({ role }) => {
    const navigate = useNavigate();
    const [searchQuery, setSearchQuery] = useState("");
    const [forumThreads, setForumThreads] = useState([]);
    const [filteredThreads, setFilteredThreads] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/threads")
            .then(response => response.json())
            .then(data => {
                setForumThreads(data);
                setFilteredThreads(data);
            });
    }, []);

    const handleSearch = (e) => {
        const query = e.target.value.toLowerCase();
        setSearchQuery(query);
        setFilteredThreads(forumThreads.filter(thread => 
            thread.title.toLowerCase().includes(query) || 
            thread.tags.some(tag => tag.toLowerCase().includes(query))
        ));
    };

    const handleUpvote = (id) => {
        fetch(`http://localhost:5000/api/threads/${id}/upvote`, { method: "POST" })
            .then(response => response.json())
            .then(updatedThread => {
                setForumThreads(prevThreads => prevThreads.map(thread => 
                    thread._id === id ? updatedThread : thread
                ));
                setFilteredThreads(prevThreads => prevThreads.map(thread => 
                    thread._id === id ? updatedThread : thread
                ));
            });
    };

    return (
        <section className="container mt-5 forum-container">
            <h1 className="text-center forum-title">Discussion Forum</h1>
            <input 
                type="text" 
                placeholder="Search discussions..." 
                className="form-control mb-3 search-input" 
                value={searchQuery} 
                onChange={handleSearch} 
            />
            <div className="row">
                {filteredThreads.map((thread, index) => (
                    <div key={index} className="col-lg-4 col-md-6 mb-4">
                        <div className="card h-100 shadow-sm forum-card">
                            <div className="card-body d-flex flex-column">
                                <h5 className="card-title forum-card-title">{thread.title}</h5>
                                <p className="card-text flex-grow-1 forum-card-text">{thread.description}</p>
                                <div className="mb-2 forum-tags">
                                    {thread.tags.map((tag, i) => (
                                        <span key={i} className="badge bg-primary me-1 forum-tag">#{tag}</span>
                                    ))}
                                </div>
                                <button 
                                    className="btn btn-primary mt-auto forum-btn" 
                                    onClick={() => navigate(`/forum/${thread._id}`)}
                                >
                                    View Discussion
                                </button>
                                <button 
                                    className="btn btn-outline-success mt-2 forum-upvote-btn" 
                                    onClick={() => handleUpvote(thread._id)}
                                >
                                    üëç {thread.upvotes}
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </section>
    );
};

export default DiscussionForum;

/* ========== CSS Styles ========== */
const styles = document.createElement("style");
styles.innerHTML = `
    .forum-container {
        max-width: 1200px;
        margin: auto;
    }

    .forum-title {
        font-size: 2rem;
        font-weight: bold;
    }

    .search-input {
        border-radius: 10px;
        padding: 10px;
        font-size: 1rem;
    }

    .forum-card {
        border-radius: 12px;
        transition: 0.3s;
    }

    .forum-card:hover {
        transform: translateY(-5px);
        box-shadow: 0 4px 10px rgba(0, 0, 0, 0.15);
    }

    .forum-card-title {
        font-weight: bold;
    }

    .forum-card-text {
        color: #555;
    }

    .forum-tags {
        margin-bottom: 10px;
    }

    .forum-tag {
        font-size: 0.9rem;
    }

    .forum-btn {
        font-size: 0.9rem;
        font-weight: bold;
    }

    .forum-upvote-btn {
        font-size: 0.9rem;
    }
`;
document.head.appendChild(styles);
