import React, { useState } from "react";
import axios from "axios";

const CreateAssessment = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [questions, setQuestions] = useState([
    { questionText: "", options: ["", "", "", ""], correctAnswer: "" },
  ]);

  const handleAddQuestion = () => {
    setQuestions([...questions, { questionText: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:5000/api/assessments/create",
        { title, description, questions },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      alert("Assessment Created Successfully");
    } catch (error) {
      console.error("Error creating assessment", error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Create Assessment</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" className="form-control mb-3" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea className="form-control mb-3" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
        {questions.map((q, i) => (
          <div key={i}>
            <input type="text" className="form-control mb-2" placeholder="Question" value={q.questionText} onChange={(e) => {
              const newQuestions = [...questions];
              newQuestions[i].questionText = e.target.value;
              setQuestions(newQuestions);
            }} required />
          </div>
        ))}
        <button type="button" className="btn btn-secondary" onClick={handleAddQuestion}>Add Question</button>
        <button type="submit" className="btn btn-primary mt-3">Create</button>
      </form>
    </div>
  );
};

export default CreateAssessment;
