import React, { useState, useEffect } from "react";
import axios from "axios";

const GeneralDiscussions = () => {
    const [discussions, setDiscussions] = useState([]);
    const [newDiscussion, setNewDiscussion] = useState({ title: "", content: "" });
    const [selectedDiscussion, setSelectedDiscussion] = useState(null);
    const [newReply, setNewReply] = useState("");

    useEffect(() => {
        fetchDiscussions();
    }, []);

    const fetchDiscussions = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/discussions");
            setDiscussions(res.data);
        } catch (error) {
            console.error("Error fetching discussions:", error);
        }
    };

    const handleCreateDiscussion = async () => {
        if (!newDiscussion.title || !newDiscussion.content) {
            return alert("Please enter all fields");
        }

        try {
            const userId = localStorage.getItem("userId");

            await axios.post("http://localhost:5000/api/discussions", {
                title: newDiscussion.title,
                content: newDiscussion.content,
                author: userId,
                category: "General Discussions",
            });

            setNewDiscussion({ title: "", content: "" });
            fetchDiscussions();
        } catch (error) {
            console.error("Error creating discussion:", error.response?.data || error);
        }
    };

    const handleDeleteDiscussion = async (discussionId) => {
        const confirmDelete = window.confirm("Are you sure you want to delete this discussion?");
        if (!confirmDelete) return;

        try {
            await axios.delete(`http://localhost:5000/api/discussions/${discussionId}`);
            setDiscussions(discussions.filter(d => d._id !== discussionId));
        } catch (error) {
            console.error("Error deleting discussion:", error);
        }
    };

    const handleReply = async (discussionId) => {
        if (!newReply) return alert("Enter a reply");
        try {
            const userId = localStorage.getItem("userId");

            await axios.post("http://localhost:5000/api/replies", {
                content: newReply,
                author: userId,
                discussion: discussionId
            });

            setNewReply("");
            setSelectedDiscussion(null); // Close reply input after posting
            fetchDiscussions();
        } catch (error) {
            console.error("Error adding reply:", error);
        }
    };

    const handleDeleteReply = async (replyId) => {
        const confirmDelete = window.confirm("Are you sure you want to delete this reply?");
        if (!confirmDelete) return;

        try {
            await axios.delete(`http://localhost:5000/api/replies/${replyId}`);
            fetchDiscussions(); // Refresh discussions to reflect reply deletion
        } catch (error) {
            console.error("Error deleting reply:", error);
        }
    };

    const handleLike = async (discussionId) => {
        try {
            const userId = localStorage.getItem("userId");

            const res = await axios.post("http://localhost:5000/api/likes", { 
                user: userId, 
                targetId: discussionId, 
                targetType: "Discussion" 
            });

            setDiscussions((prevDiscussions) =>
                prevDiscussions.map((discussion) =>
                    discussion._id === discussionId 
                        ? { ...discussion, likes: [...discussion.likes, userId] }
                        : discussion
                )
            );

        } catch (error) {
            console.error("Error liking discussion:", error.response?.data || error);

            if (error.response?.data?.message === "Already liked") {
                alert("User already liked this discussion.");
            }
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>💬 General Discussions</h1>

            <div style={styles.form}>
                <input
                    type="text"
                    placeholder="Discussion Title"
                    value={newDiscussion.title}
                    onChange={(e) => setNewDiscussion({ ...newDiscussion, title: e.target.value })}
                    style={styles.input}
                />
                <textarea
                    placeholder="Discussion Content"
                    value={newDiscussion.content}
                    onChange={(e) => setNewDiscussion({ ...newDiscussion, content: e.target.value })}
                    style={styles.textarea}
                />
                <button onClick={handleCreateDiscussion} style={styles.button}>Start Discussion</button>
            </div>

            <div style={styles.discussionsList}>
                {discussions.map((discussion) => (
                    <div key={discussion._id} style={styles.discussionCard}>
                        <div style={styles.header}>
                            <h3>{discussion.title}</h3>
                            <span style={styles.deleteIcon} onClick={() => handleDeleteDiscussion(discussion._id)}>🗑️</span>
                        </div>
                        <p>{discussion.content}</p>
                        <p><strong>Author:</strong> {discussion.author?.fullName || "Unknown"}</p>
                        <div style={styles.actions}>
                            <button onClick={() => handleLike(discussion._id)} style={styles.likeButton}>👍 {discussion.likes?.length || 0}</button>
                            {selectedDiscussion !== discussion._id && (
                                <button onClick={() => setSelectedDiscussion(discussion._id)} style={styles.replyButton}>💬 Reply</button>
                            )}
                        </div>

                        <div style={styles.replySection}>
                            {discussion.replies?.map((reply) => (
                                <div key={reply._id} style={styles.reply}>
                                    <p>
                                        <strong>{reply.author?.fullName || "Anonymous"}:</strong> {reply.content}
                                    </p>
                                    <span style={styles.deleteReply} onClick={() => handleDeleteReply(reply._id)}>❌</span>
                                </div>
                            ))}
                        </div>

                        {selectedDiscussion === discussion._id && (
                            <div style={styles.replyInputSection}>
                                <textarea
                                    placeholder="Write a reply..."
                                    value={newReply}
                                    onChange={(e) => setNewReply(e.target.value)}
                                    style={styles.textarea}
                                />
                                <div style={styles.actions}>
                                    <button onClick={() => handleReply(discussion._id)} style={styles.replyButton}>➤ Submit Reply</button>
                                    <button onClick={() => setSelectedDiscussion(null)} style={styles.cancelButton}>❌ Cancel</button>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

const styles = {
    container: { marginLeft:"250px", padding: "30px", minHeight: "100vh", backgroundColor: "#f8f9fa", display: "flex", flexDirection: "column", alignItems: "center" },
    title: { fontSize: "24px", fontWeight: "bold", color: "#003c8f" },
    form: { background: "#fff", padding: "20px", borderRadius: "12px", boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.15)", width: "90%", maxWidth: "600px", display: "flex", flexDirection: "column", gap: "10px" },
    input: { width: "100%", padding: "10px", borderRadius: "8px", fontSize: "16px" },
    textarea: { width: "100%", padding: "10px", borderRadius: "8px", fontSize: "16px" },
    button: { padding: "10px", borderRadius: "8px", background: "#003c8f", color: "white", fontSize: "16px" },
    deleteReply: { cursor: "pointer", color: "red", fontWeight: "bold" },
    cancelButton: { padding: "6px 10px", borderRadius: "8px", background: "red", color: "white" },
};

export default GeneralDiscussions;
