import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";
import OngoingAssessments from "./OngoingAssessments";

const Assessments = () => {
  const navigate = useNavigate();
  const [viewOngoing, setViewOngoing] = useState(false);
  const [role, setRole] = useState(null);

  // Fetch role from localStorage
  useEffect(() => {
    const storedRole = localStorage.getItem("role");
    setRole(storedRole);
  }, []);

  // Loading state until role is fetched
  if (role === null) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  // Assessments for students
  const studentItems = [
    { title: "📝 Ongoing Assessments", desc: "View and complete active assessments.", path: "ongoing" },
    { title: "✅ Completed Assessments", desc: "Review your past submissions and scores.", path: "/assessments/completed" },
    { title: "📊 Performance Analytics", desc: "Analyze your strengths and areas for improvement.", path: "/assessments/analytics" },
  ];

  // Assessments for admins
  const adminItems = [
    { title: "📌 Create New Assessment", desc: "Design and assign assessments to students.", path: "/assessments/create" }
  ];

  return (
    <>
      {/* Embedded CSS to prevent merging with the sidebar and ensure proper spacing */}
      <style>
        {`
          .main-content {
            margin-left: 1000px; /* Adjust according to sidebar width */
            padding: 20px;
          }

          .container {
            max-width: 1200px;
            margin: auto;
            padding: 20px;
          }

          .card {
            border-radius: 10px;
            transition: transform 0.2s ease-in-out;
            background-color: #ffffff;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
          }

          .card:hover {
            transform: scale(1.05);
          }

          .card-body {
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            padding: 20px;
          }

          .btn-primary {
            background-color: #007bff;
            border: none;
            border-radius: 5px;
            padding: 10px 15px;
            font-size: 1rem;
            transition: background-color 0.3s;
          }

          .btn-primary:hover {
            background-color: #0056b3;
          }

          /* Ensure admin items take full width */
          .admin-card {
            max-width: 600px;
            margin: auto;
          }

          @media screen and (max-width: 768px) {
            .main-content {
              margin-left: 0;
              padding: 15px;
            }

            .container {
              padding: 10px;
            }

            .admin-card {
              max-width: 100%; /* Make it full width on smaller screens */
            }
          }
        `}
      </style>

      {role === "admin" ? (
        // Admin View: Show Only "Create Assessment"
        <section className="container main-content mt-5">
          <h1 className="text-center">Assessments</h1>
          <div className="d-flex justify-content-center">
            {adminItems.map((item, index) => (
              <div key={index} className="admin-card">
                <div className="card h-100 shadow-sm">
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title">{item.title}</h5>
                    <p className="card-text flex-grow-1">{item.desc}</p>
                    <button className="btn btn-primary mt-auto" onClick={() => navigate(item.path)}>
                      Create Now
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      ) : viewOngoing ? (
        // Student View: Show Ongoing Assessments inside the same page
        <OngoingAssessments />
      ) : (
        // Student View: Show Ongoing, Completed, and Analytics
        <section className="container main-content mt-5">
          <h1 className="text-center">Assessments</h1>
          <div className="row">
            {studentItems.map((item, index) => (
              <div key={index} className="col-lg-4 col-md-6 mb-4">
                <div className="card h-100 shadow-sm">
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title">{item.title}</h5>
                    <p className="card-text flex-grow-1">{item.desc}</p>
                    <button
                      className="btn btn-primary mt-auto"
                      onClick={() => item.path === "ongoing" ? setViewOngoing(true) : navigate(item.path)}
                    >
                      View Details
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}
    </>
  );
};

export default Assessments;
